        return { id: 1, name: 'Mock Data' };
    }
    switch (schema.type) {
        case 'object': {
            const obj = {};
            if (schema.properties) {
                for (const [key, propSchema] of Object.entries(schema.properties)) {
                    obj[key] = generateMockFromSchema(propSchema);
