      if (typeof operation === 'object' && operation.operationId) {
        lines.push(`  async ${operation.operationId}(params?: any): Promise<any> {`);
        if (options.outputFormat === 'axios') {
          lines.push(`    return this.client.${method}('${path}', params);`);
        } else {
          lines.push(`    const response = await fetch(\`\${this.baseURL}${path}\`, {`);
          lines.push(`      method: '${method.toUpperCase()}',`);
          lines.push(`      headers: { 'Content-Type': 'application/json' },`);
          lines.push(`      body: JSON.stringify(params)`);
          lines.push(`    });`);
          lines.push(`    return response.json();`);
        }
        lines.push('  }');
        lines.push('');
      }
    }
  }

  lines.push('}');

  return lines.join('\n');
}

/**
 * Generate TypeScript GraphQL client
 */
export function generateTypeScriptGraphQLClient(
  _schema: GraphQLSchema,
  _options: ClientGenerationOptions
): string {
  const lines: string[] = [];

  lines.push('// GraphQL client');
  lines.push('export class GraphQLClient {');
  lines.push('  constructor(private endpoint: string) {}');
  lines.push('');
  lines.push('  async query(query: string, variables?: any): Promise<any> {');
  lines.push('    const response = await fetch(this.endpoint, {');
  lines.push("      method: 'POST',");
  lines.push("      headers: { 'Content-Type': 'application/json' },");
  lines.push('      body: JSON.stringify({ query, variables })');
  lines.push('    });');
  lines.push('    return response.json();');
  lines.push('  }');
  lines.push('}');

  return lines.join('\n');
}

/**
 * Generate Python REST client
 */
export function generatePythonRestClient(
  spec: OpenAPISpec,
  _options: ClientGenerationOptions
): string {
  const lines: string[] = [];

  lines.push('import requests');
  lines.push('from typing import Optional, Dict, Any');
  lines.push('');
  lines.push(`class ${spec.info.title.replace(/\s+/g, '')}Client:`);
  lines.push('    def __init__(self, base_url: str):');
  lines.push('        self.base_url = base_url');
  lines.push('');

  for (const [path, methods] of Object.entries(spec.paths)) {
    for (const [method, operation] of Object.entries(methods)) {
      if (typeof operation === 'object' && operation.operationId) {
        lines.push(
          `    def ${operation.operationId}(self, params: Optional[Dict[str, Any]] = None):`
        );
        lines.push(
          `        return requests.${method}(f"{{self.base_url}}${path}", json=params).json()`
        );
        lines.push('');
      }
    }
  }

  return lines.join('\n');
}

/**
 * Map JSON Schema type to TypeScript type
 */
function mapSchemaToTSType(schema: any): string {
  if (!schema) return 'any';

  switch (schema.type) {
    case 'string':
      return schema.enum ? schema.enum.map((v: any) => `'${v}'`).join(' | ') : 'string';
    case 'number':
    case 'integer':
      return 'number';
    case 'boolean':
      return 'boolean';
    case 'array':
      return `${mapSchemaToTSType(schema.items)}[]`;
    case 'object':
      return schema.additionalProperties ? 'Record<string, any>' : 'object';
    default:
      return schema.$ref ? schema.$ref.split('/').pop() || 'any' : 'any';
  }
}

import { OpenAPISpec, MockServerConfig } from '../types.js';
import express, { Request, Response, NextFunction } from 'express';
import cors from 'cors';

/**
 * Generate mock server based on OpenAPI spec
 */
export function generateMockServerCode(spec: OpenAPISpec, config: MockServerConfig): string {
  const lines: string[] = [];

  lines.push("import express from 'express';");
  lines.push("import cors from 'cors';");
  lines.push('');
  lines.push('const app = express();');
  lines.push('app.use(cors());');
  lines.push('app.use(express.json());');
  lines.push('');

  // Generate routes for each path
  for (const [path, methods] of Object.entries(spec.paths)) {
    for (const [method, operation] of Object.entries(methods)) {
      if (typeof operation === 'object') {
        const mockResponse = generateMockResponse(operation);
        const expressPath = path.replace(/{(\w+)}/g, ':$1');

        lines.push(`app.${method}('${expressPath}', (req, res) => {`);
        if (config.responseDelay) {
          lines.push(`  setTimeout(() => {`);
          lines.push(`    res.json(${JSON.stringify(mockResponse)});`);
          lines.push(`  }, ${config.responseDelay});`);
        } else {
          lines.push(`  res.json(${JSON.stringify(mockResponse)});`);
        }
        lines.push(`});`);
        lines.push('');
      }
    }
  }

  const port = config.port || 3000;
  lines.push(`const PORT = process.env.PORT || ${port};`);
  lines.push('app.listen(PORT, () => {');
  lines.push(`  console.log(\`Mock server running on port \${PORT}\`);`);
  lines.push('});');

  return lines.join('\n');
}

/**
 * Create and start mock server
 */
export function createMockServer(spec: OpenAPISpec, config: MockServerConfig) {
  const app = express();

  if (config.includeCORS) {
    app.use(cors());
  }

  app.use(express.json());

  if (config.includeLogging) {
    app.use((req: Request, _res: Response, next: NextFunction) => {
      // Sanitize request data to prevent log injection
      const method = String(req.method).replace(/[\r\n]/g, '');
      const safePath = String(req.path)
        .replace(/[\r\n]/g, '')
        .substring(0, 200);
      // eslint-disable-next-line no-console
      console.log(`[${new Date().toISOString()}] ${method} ${safePath}`);
      next();
    });
  }

  // Generate routes
  for (const [path, methods] of Object.entries(spec.paths)) {
    for (const [method, operation] of Object.entries(methods)) {
      if (typeof operation === 'object') {
        const mockResponse = generateMockResponse(operation);
        const expressPath = path.replace(/{(\w+)}/g, ':$1');

        (app as any)[method](expressPath, (_req: any, res: any) => {
          if (config.responseDelay) {
            setTimeout(() => {
              res.json(mockResponse);
            }, config.responseDelay);
          } else {
            res.json(mockResponse);
          }
        });
      }
    }
  }

  const port = config.port || 3000;
  app.listen(port, () => {
    // eslint-disable-next-line no-console
    console.log(`Mock server running on port ${Number(port)}`);
  });

  return app;
}

/**
 * Generate mock response based on operation schema
 */
function generateMockResponse(operation: any): any {
  if (operation.responses?.['200']?.content?.['application/json']?.schema) {
    const schema = operation.responses['200'].content['application/json'].schema;
    return generateMockFromSchema(schema);
  }

  return { success: true, message: 'Mock response' };
}

/**
 * Generate mock data from JSON Schema
 */
function generateMockFromSchema(schema: any): any {
  if (!schema) return null;

  if (schema.$ref) {
    return { id: 1, name: 'Mock Data' };
  }

  switch (schema.type) {
    case 'object': {
      const obj: any = {};
      if (schema.properties) {
        for (const [key, propSchema] of Object.entries(schema.properties)) {
          obj[key] = generateMockFromSchema(propSchema);
        }
      }
      return obj;
    }

    case 'array':
      return [generateMockFromSchema(schema.items)];

    case 'string':
      return schema.enum ? schema.enum[0] : 'mock-string';

    case 'number':
    case 'integer':
      return 42;

    case 'boolean':
      return true;

    default:
      return null;
  }
}

/**
 * OpenAPI Specification Generator
 */

import { APIDesignConfig, OpenAPISpec, RESTEndpoint, APIDesignResult } from '../types.js';

const OPENAPI_VERSION = '3.0.3';
const DEFAULT_PAGE_SIZE = 20;
const MAX_PAGE_SIZE = 100;
const HTTP_STATUS_OK = 200;
const HTTP_STATUS_CREATED = 201;
const HTTP_STATUS_NO_CONTENT = 204;
const HTTP_STATUS_BAD_REQUEST = 400;
const HTTP_STATUS_NOT_FOUND = 404;

/**
 * Generate OpenAPI 3.0 specification from configuration
 */
export function generateOpenAPISpec(
  config: APIDesignConfig,
  endpoints?: RESTEndpoint[]
): APIDesignResult {
  try {
    const spec: OpenAPISpec = {
      openapi: OPENAPI_VERSION,
      info: {
        title: config.name,
        version: config.version,
        description: config.description || `API specification for ${config.name}`,
        contact: {
          name: 'API Support',
          email: 'support@example.com',
        },
        license: {
          name: 'MIT',
          url: 'https://opensource.org/licenses/MIT',
        },
      },
      servers: config.baseUrl
        ? [
            { url: config.baseUrl, description: 'Production server' },
            { url: 'https://staging-api.example.com/v1', description: 'Staging server' },
          ]
        : [
            { url: 'https://api.example.com/v1', description: 'Production server' },
            { url: 'https://staging-api.example.com/v1', description: 'Staging server' },
          ],
      paths: {},
      components: {
        schemas: {},
        securitySchemes: {},
      },
      tags: [],
    };

    // Add authentication if configured
    if (config.auth && config.auth.type !== 'none') {
      addAuthenticationScheme(spec, config);
    }

    // Generate endpoints from resources if not provided
    if (!endpoints && config.resources) {
      endpoints = generateRESTEndpointsFromResources(config.resources, config);
    }

    // Add endpoints to paths
    if (endpoints && endpoints.length > 0) {
      addCustomEndpointsWithTags(spec, endpoints);
    }

    return {
      success: true,
      data: spec,
      metadata: {
        endpointCount: endpoints?.length || 0,
        resourceCount: config.resources?.length || 0,
        generatedAt: new Date().toISOString(),
        version: config.version,
      },
    };
  } catch (error) {
    return {
      success: false,
      errors: [error instanceof Error ? error.message : 'Unknown error'],
    };
  }
}

function addAuthenticationScheme(spec: OpenAPISpec, config: APIDesignConfig): void {
  if (!config.auth || !spec.components) return;

  switch (config.auth.type) {
    case 'bearer':
      spec.components.securitySchemes!.BearerAuth = {
        type: 'http',
        scheme: 'bearer',
        bearerFormat: 'JWT',
      };
      spec.security = [{ BearerAuth: [] }];
      break;
    case 'apiKey':
      spec.components.securitySchemes!.ApiKeyAuth = {
        type: 'apiKey',
        in: 'header',
        name: 'X-API-Key',
      };
      spec.security = [{ ApiKeyAuth: [] }];
      break;
    case 'oauth2':
      spec.components.securitySchemes!.OAuth2 = {
        type: 'oauth2',
        flows: {
          authorizationCode: {
            authorizationUrl: 'https://api.example.com/oauth/authorize',
            tokenUrl: 'https://api.example.com/oauth/token',
            scopes: {
              'read:users': 'Read user data',
              'write:users': 'Modify user data',
            },
          },
        },
      };
      spec.security = [{ OAuth2: ['read:users', 'write:users'] }];
      break;
  }
}

function addCustomEndpointsWithTags(spec: OpenAPISpec, endpoints: RESTEndpoint[]): void {
  for (const endpoint of endpoints) {
    // Prevent prototype pollution: reject dangerous keys
    if (
      endpoint.path === '__proto__' ||
      endpoint.path === 'constructor' ||
      endpoint.path === 'prototype'
    ) {
      continue;
    }

    if (!Object.prototype.hasOwnProperty.call(spec.paths, endpoint.path)) {
      spec.paths[endpoint.path] = {};
    }

    const method = endpoint.method.toLowerCase();
    // Additional safety check for method
    if (method === '__proto__' || method === 'constructor' || method === 'prototype') {
      continue;
    }

    spec.paths[endpoint.path][method] = {
      summary: endpoint.summary,
      description: endpoint.description,
      operationId: endpoint.operationId,
      tags: endpoint.tags,
      parameters: endpoint.parameters,
      requestBody: endpoint.requestBody,
      responses: endpoint.responses,
      security: endpoint.security,
      deprecated: endpoint.deprecated,
    };

    // Add tags
    if (endpoint.tags && spec.tags) {
      for (const tag of endpoint.tags) {
        if (!spec.tags.find(t => t.name === tag)) {
          spec.tags.push({
            name: tag,
            description: `Operations related to ${tag}`,
          });
        }
      }
    }
  }
}

function capitalize(str: string): string {
  return str.charAt(0).toUpperCase() + str.slice(1);
}

/**
 * Generate REST endpoints from resource names
 * Creates full CRUD operations for each resource
 */
export function generateRESTEndpointsFromResources(
  resources: string[],
  _config: APIDesignConfig
): RESTEndpoint[] {
  const endpoints: RESTEndpoint[] = [];

  for (const resource of resources) {
    const resourcePath = `/${resource}`;
    const resourceItemPath = `/${resource}/{id}`;
    const resourceName = capitalize(resource);
    const singularName = resource.endsWith('s') ? resource.slice(0, -1) : resource;

    // List operation
    endpoints.push({
      path: resourcePath,
      method: 'GET',
      summary: `List all ${resource}`,
      description: `Retrieve a paginated list of ${resource}`,
      operationId: `list${resourceName}`,
      tags: [resourceName],
      parameters: [
        {
          name: 'page',
          type: 'integer',
          required: false,
          description: 'Page number for pagination',
          in: 'query',
          default: 1,
          minimum: 1,
        },
        {
          name: 'limit',
          type: 'integer',
          required: false,
          description: 'Number of items per page',
          in: 'query',
          default: DEFAULT_PAGE_SIZE,
          minimum: 1,
          maximum: MAX_PAGE_SIZE,
        },
        {
          name: 'sort',
          type: 'string',
          required: false,
          description: 'Sort field and order (e.g., "createdAt:desc")',
          in: 'query',
        },
      ],
      responses: {
        '200': {
          statusCode: HTTP_STATUS_OK,
          description: 'Successful response',
          contentType: 'application/json',
          schema: {
            type: 'object',
            properties: {
              data: {
                type: 'array',
                items: { type: 'object', $ref: `#/components/schemas/${resourceName}` },
              },
              pagination: {
                type: 'object',
                properties: {
                  page: { type: 'integer' },
                  limit: { type: 'integer' },
                  total: { type: 'integer' },
                  totalPages: { type: 'integer' },
                },
              },
            },
          },
        },
        '400': {
          statusCode: HTTP_STATUS_BAD_REQUEST,
          description: 'Bad request',
          schema: { type: 'object', $ref: '#/components/schemas/Error' },
        },
      },
    });

    // Create operation
    endpoints.push({
      path: resourcePath,
      method: 'POST',
      summary: `Create a new ${singularName}`,
      description: `Create a new ${singularName} resource`,
      operationId: `create${resourceName}`,
      tags: [resourceName],
      requestBody: {
        required: true,
        description: `${resourceName} object to create`,
        content: {
          'application/json': {
            schema: { type: 'object', $ref: `#/components/schemas/${resourceName}Input` },
          },
        },
      },
      responses: {
        '201': {
          statusCode: HTTP_STATUS_CREATED,
          description: 'Resource created successfully',
          schema: { type: 'object', $ref: `#/components/schemas/${resourceName}` },
        },
        '400': {
          statusCode: HTTP_STATUS_BAD_REQUEST,
          description: 'Invalid input',
          schema: { type: 'object', $ref: '#/components/schemas/Error' },
        },
      },
    });

    // Get by ID operation
    endpoints.push({
      path: resourceItemPath,
      method: 'GET',
      summary: `Get ${singularName} by ID`,
      description: `Retrieve a specific ${singularName} by its ID`,
      operationId: `get${resourceName}ById`,
      tags: [resourceName],
      parameters: [
        {
          name: 'id',
          type: 'string',
          required: true,
          description: `${resourceName} identifier`,
          in: 'path',
        },
      ],
      responses: {
        '200': {
          statusCode: HTTP_STATUS_OK,
          description: 'Successful response',
          schema: { type: 'object', $ref: `#/components/schemas/${resourceName}` },
        },
        '404': {
          statusCode: HTTP_STATUS_NOT_FOUND,
          description: 'Resource not found',
          schema: { type: 'object', $ref: '#/components/schemas/Error' },
        },
      },
    });

    // Update operation
    endpoints.push({
      path: resourceItemPath,
      method: 'PUT',
      summary: `Update ${singularName}`,
      description: `Update an existing ${singularName} resource`,
      operationId: `update${resourceName}`,
      tags: [resourceName],
      parameters: [
        {
          name: 'id',
          type: 'string',
          required: true,
          description: `${resourceName} identifier`,
          in: 'path',
        },
      ],
      requestBody: {
        required: true,
        description: `Updated ${singularName} data`,
        content: {
          'application/json': {
            schema: { type: 'object', $ref: `#/components/schemas/${resourceName}Input` },
          },
        },
      },
      responses: {
        '200': {
          statusCode: HTTP_STATUS_OK,
          description: 'Resource updated successfully',
          schema: { type: 'object', $ref: `#/components/schemas/${resourceName}` },
        },
        '404': {
          statusCode: HTTP_STATUS_NOT_FOUND,
          description: 'Resource not found',
          schema: { type: 'object', $ref: '#/components/schemas/Error' },
        },
      },
    });

    // Delete operation
    endpoints.push({
      path: resourceItemPath,
      method: 'DELETE',
      summary: `Delete ${singularName}`,
      description: `Delete a ${singularName} resource`,
      operationId: `delete${resourceName}`,
      tags: [resourceName],
      parameters: [
        {
          name: 'id',
          type: 'string',
          required: true,
          description: `${resourceName} identifier`,
          in: 'path',
        },
      ],
      responses: {
        '204': {
          statusCode: HTTP_STATUS_NO_CONTENT,
          description: 'Resource deleted successfully',
        },
        '404': {
          statusCode: HTTP_STATUS_NOT_FOUND,
          description: 'Resource not found',
          schema: { type: 'object', $ref: '#/components/schemas/Error' },
        },
      },
    });
  }

  return endpoints;
}

/**
 * Type definitions for API Designer MCP
 * Comprehensive types for REST, GraphQL, and OpenAPI design
 */

/**
 * HTTP methods supported by REST APIs
 */
export type HTTPMethod = 'GET' | 'POST' | 'PUT' | 'PATCH' | 'DELETE' | 'OPTIONS' | 'HEAD';

/**
 * Authentication types supported
 */
export type AuthType = 'none' | 'apiKey' | 'bearer' | 'oauth2' | 'basic';

/**
 * API design styles
 */
export type APIStyle = 'REST' | 'GraphQL' | 'gRPC' | 'WebSocket';

/**
 * Data types for API parameters and responses
 */
export type DataType = 'string' | 'number' | 'integer' | 'boolean' | 'object' | 'array' | 'file';

/**
 * Parameter location in HTTP request
 */
export type ParameterLocation = 'query' | 'path' | 'header' | 'cookie' | 'body';

/**
 * Response content types
 */
export type ContentType =
  | 'application/json'
  | 'application/xml'
  | 'text/plain'
  | 'multipart/form-data'
  | 'application/octet-stream';

/**
 * API parameter definition
 */
export interface APIParameter {
  name: string;
  type: DataType;
  required?: boolean;
  description?: string;
  default?: any;
  example?: any;
  enum?: any[];
  format?: string;
  pattern?: string;
  minimum?: number;
  maximum?: number;
  minLength?: number;
  maxLength?: number;
}

/**
 * API response definition
 */
export interface APIResponse {
  statusCode: number;
  description: string;
  contentType?: ContentType;
  schema?: APISchema;
  examples?: Record<string, any>;
  headers?: Record<string, APIParameter>;
}

/**
 * API schema definition (JSON Schema compatible)
 */
export interface APISchema {
  type: DataType;
  properties?: Record<string, APISchema>;
  items?: APISchema;
  required?: string[];
  description?: string;
  example?: any;
  enum?: any[];
  format?: string;
  pattern?: string;
  minimum?: number;
  maximum?: number;
  minLength?: number;
  maxLength?: number;
  additionalProperties?: boolean | APISchema;
  allOf?: APISchema[];
  anyOf?: APISchema[];
  oneOf?: APISchema[];
  not?: APISchema;
  $ref?: string;
}

/**
 * REST API endpoint definition
 */
export interface RESTEndpoint {
  path: string;
  method: HTTPMethod;
  summary: string;
  description?: string;
  operationId?: string;
  tags?: string[];
  parameters?: Array<APIParameter & { in: ParameterLocation }>;
  requestBody?: {
    required?: boolean;
    description?: string;
    content: Partial<Record<ContentType, { schema: APISchema; examples?: Record<string, any> }>>;
  };
  responses: Record<string, APIResponse>;
  security?: Array<Record<string, string[]>>;
  deprecated?: boolean;
}

/**
 * GraphQL field definition
 */
export interface GraphQLField {
  name: string;
  type: string;
  description?: string;
  args?: GraphQLArgument[];
  resolve?: string;
  deprecated?: boolean;
  deprecationReason?: string;
}

/**
 * GraphQL argument definition
 */
export interface GraphQLArgument {
  name: string;
  type: string;
  description?: string;
  defaultValue?: any;
}

/**
 * GraphQL type definition
 */
export interface GraphQLType {
  name: string;
  kind: 'object' | 'input' | 'interface' | 'union' | 'enum' | 'scalar';
  description?: string;
  fields?: GraphQLField[];
  interfaces?: string[];
  possibleTypes?: string[];
  enumValues?: Array<{ name: string; description?: string; deprecated?: boolean }>;
}

/**
 * GraphQL schema definition
 */
export interface GraphQLSchema {
  types: GraphQLType[];
  queries?: GraphQLField[];
  mutations?: GraphQLField[];
  subscriptions?: GraphQLField[];
  directives?: Array<{
    name: string;
    description?: string;
    locations: string[];
    args?: GraphQLArgument[];
  }>;
}

/**
 * OpenAPI 3.0 specification
 */
export interface OpenAPISpec {
  openapi: string;
  info: {
    title: string;
    version: string;
    description?: string;
    contact?: {
      name?: string;
      email?: string;
      url?: string;
    };
    license?: {
      name: string;
      url?: string;
    };
  };
  servers?: Array<{
    url: string;
    description?: string;
    variables?: Record<
      string,
      {
        default: string;
        enum?: string[];
        description?: string;
      }
    >;
  }>;
  paths: Record<string, Record<string, any>>;
  components?: {
    schemas?: Record<string, APISchema>;
    responses?: Record<string, APIResponse>;
    parameters?: Record<string, APIParameter>;
    securitySchemes?: Record<string, any>;
    requestBodies?: Record<string, any>;
    headers?: Record<string, any>;
    examples?: Record<string, any>;
  };
  security?: Array<Record<string, string[]>>;
  tags?: Array<{
    name: string;
    description?: string;
    externalDocs?: {
      url: string;
      description?: string;
    };
  }>;
  externalDocs?: {
    url: string;
    description?: string;
  };
}

/**
 * API design configuration
 */
export interface APIDesignConfig {
  name: string;
  version: string;
  description?: string;
  baseUrl?: string;
  style: APIStyle;
  auth?: {
    type: AuthType;
    config?: Record<string, any>;
  };
  resources?: string[];
  features?: string[];
  conventions?: {
    namingCase?: 'camelCase' | 'snake_case' | 'kebab-case' | 'PascalCase';
    versioning?: 'url' | 'header' | 'query';
    pagination?: 'offset' | 'cursor' | 'page';
    errorFormat?: 'rfc7807' | 'custom';
  };
}

/**
 * API client generation options
 */
export interface ClientGenerationOptions {
  language: 'typescript' | 'javascript' | 'python' | 'java' | 'csharp' | 'go' | 'ruby' | 'php';
  outputFormat?: 'axios' | 'fetch' | 'sdk' | 'openapi-generator';
  includeTypes?: boolean;
  includeTests?: boolean;
  asyncStyle?: 'promise' | 'async-await' | 'callback';
  errorHandling?: 'throw' | 'return' | 'callback';
}

/**
 * Mock server configuration
 */
export interface MockServerConfig {
  port?: number;
  framework?: 'express' | 'fastify' | 'koa' | 'hapi';
  middleware?: string[];
  responseDelay?: number;
  includeCORS?: boolean;
  includeLogging?: boolean;
  dataGeneration?: 'faker' | 'custom' | 'examples';
}

/**
 * API validation result
 */
export interface ValidationResult {
  valid: boolean;
  errors: ValidationError[];
  warnings: ValidationWarning[];
  suggestions?: string[];
}

/**
 * Validation error
 */
export interface ValidationError {
  path: string;
  message: string;
  severity: 'error';
  code?: string;
  suggestion?: string;
}

/**
 * Validation warning
 */
export interface ValidationWarning {
  path: string;
  message: string;
  severity: 'warning';
  code?: string;
  suggestion?: string;
}

/**
 * API design result
 */
export interface APIDesignResult {
  success: boolean;
  data?: any;
  errors?: string[];
  warnings?: string[];
  metadata?: {
    endpointCount?: number;
    resourceCount?: number;
    schemaCount?: number;
    generatedAt?: string;
    version?: string;
  };
}

import { OpenAPISpec, GraphQLSchema, ValidationResult } from '../types.js';

/**
 * Validate API design against best practices
 *
 * @param spec - OpenAPI specification or GraphQL schema
