/**
 * Claude Code config generator
 */

import type { WizardSelections } from '../wizard.js';

const MCP_PACKAGES: Record<string, string> = {
  'smart-reviewer': '@j0kz/smart-reviewer-mcp',
  'test-generator': '@j0kz/test-generator-mcp',
  'architecture-analyzer': '@j0kz/architecture-analyzer-mcp',
  'doc-generator': '@j0kz/doc-generator-mcp',
  'security-scanner': '@j0kz/security-scanner-mcp',
  'refactor-assistant': '@j0kz/refactor-assistant-mcp',
  'api-designer': '@j0kz/api-designer-mcp',
  'db-schema': '@j0kz/db-schema-mcp',
};

export function generateClaudeCodeConfig(selections: WizardSelections): any {
  const config: any = {
    mcpServers: {},
  };

  for (const mcp of selections.mcps) {
    const packageName = MCP_PACKAGES[mcp];
    if (!packageName) continue;

    config.mcpServers[mcp] = {
      command: 'npx',
      args: [`${packageName}@^1.0.0`],
    };
  }

  return config;
}

/**
 * Cursor config generator
 * Uses same format as Claude Code
 */

import { generateClaudeCodeConfig } from './claude-code.js';
import type { WizardSelections } from '../wizard.js';

export function generateCursorConfig(selections: WizardSelections): any {
  // Cursor uses the same config format as Claude Code
  return generateClaudeCodeConfig(selections);
}

/**
 * Config generators index
 */

import { generateClaudeCodeConfig } from './claude-code.js';
import { generateCursorConfig } from './cursor.js';
import type { WizardSelections } from '../wizard.js';
import type { SupportedEditor } from '../detectors/editor.js';

export async function generateConfig(selections: WizardSelections): Promise<any> {
  const editor = selections.editor as SupportedEditor;

  switch (editor) {
    case 'claude-code':
      return generateClaudeCodeConfig(selections);

    case 'cursor':
      return generateCursorConfig(selections);

    case 'windsurf':
      // Windsurf uses same format as Cursor/Claude Code
      return generateCursorConfig(selections);

    case 'vscode':
      // VS Code with Continue uses similar format
      return { mcp: generateClaudeCodeConfig(selections).mcpServers };

    case 'roo':
      // Roo Code uses same format
      return generateClaudeCodeConfig(selections);

    default:
      throw new Error(`Unsupported editor: ${editor}`);
  }
}

