/**
 * Seed Data Generator Module
 * Generates realistic mock data for testing
 */

import { SQLTable, MongoCollection } from '../types.js';
import { MOCK_DATA_RANGES } from '../constants/schema-limits.js';

export function generateSQLRecords(table: SQLTable, count: number): Record<string, any>[] {
  const records: Record<string, any>[] = [];

  for (let i = 0; i < count; i++) {
    const record: Record<string, any> = {};

    for (const col of table.columns) {
      if (
        col.autoIncrement ||
        col.defaultValue === 'gen_random_uuid()' ||
        col.defaultValue === 'CURRENT_TIMESTAMP'
      ) {
        continue; // Skip auto-generated fields
      }

      record[col.name] = generateMockValue(col.name, col.type, i);
    }

    records.push(record);
  }

  return records;
}

export function generateMongoRecords(
  collection: MongoCollection,
  count: number
): Record<string, any>[] {
  const records: Record<string, any>[] = [];

  for (let i = 0; i < count; i++) {
    const record: Record<string, any> = {};

    for (const field of collection.fields) {
      if (field.name === '_id' || field.default === 'Date.now') {
        continue; // Skip auto-generated fields
      }

      record[field.name] = generateMockValue(field.name, field.type, i);
    }

    records.push(record);
  }

  return records;
}

export function generateMockValue(name: string, type: string, index: number): any {
  const lowerName = name.toLowerCase();

  if (lowerName.includes('email')) {
    return `user${index}@example.com`;
  }
  if (lowerName.includes('name')) {
    return `Test ${name} ${index}`;
  }
  if (lowerName.includes('password')) {
    return '$2b$10$hashedpassword' + index;
  }
  if (lowerName.includes('price') || lowerName.includes('amount')) {
    return (Math.random() * MOCK_DATA_RANGES.MAX_RANDOM_PRICE).toFixed(2);
  }
  if (lowerName.includes('stock') || lowerName.includes('quantity')) {
    return Math.floor(Math.random() * MOCK_DATA_RANGES.MAX_RANDOM_QUANTITY);
  }
  if (lowerName.includes('description')) {
    return `This is a sample description for item ${index}`;
  }

  // Type-based defaults
  if (type.includes('INT') || type === 'Number') {
    return index + MOCK_DATA_RANGES.SEQUENCE_OFFSET;
  }
  if (type.includes('BOOL') || type === 'Boolean') {
    return index % 2 === 0;
  }
  if (type.includes('CHAR') || type === 'String') {
    return `value_${index}`;
  }
  if (type === 'DECIMAL' || type === 'NUMERIC') {
    return (index * MOCK_DATA_RANGES.DECIMAL_MULTIPLIER).toFixed(2);
  }

  return `mock_${index}`;
}

/**
 * Index Optimization Helpers
 * Functions for suggesting index optimizations
 */

import { SQLTable, IndexSuggestion } from '../types.js';
import { INDEX_PRIORITY, INDEX_IMPACT } from '../constants/schema-limits.js';

/**
 * Check for foreign keys without indexes
 */
export function suggestForeignKeyIndexes(
  table: SQLTable,
  indexedColumns: Set<string>
): IndexSuggestion[] {
  const suggestions: IndexSuggestion[] = [];
  const foreignKeys = table.foreignKeys || [];

  for (const fk of foreignKeys) {
    if (!indexedColumns.has(fk.column)) {
      suggestions.push({
        table: table.name,
        columns: [fk.column],
        type: 'BTREE',
        reason: `Foreign key column '${fk.column}' should be indexed to improve JOIN performance`,
        estimatedImpact: INDEX_IMPACT.HIGH,
        priority: INDEX_PRIORITY.HIGH,
      });
    }
  }

  return suggestions;
}

/**
 * Check for commonly filtered columns
 */
export function suggestFilterColumnIndexes(
  table: SQLTable,
  indexedColumns: Set<string>
): IndexSuggestion[] {
  const suggestions: IndexSuggestion[] = [];

  for (const column of table.columns) {
    if (
      (column.name.includes('status') ||
        column.name.includes('type') ||
        column.name.includes('category')) &&
      !indexedColumns.has(column.name)
    ) {
      suggestions.push({
        table: table.name,
        columns: [column.name],
        type: 'BTREE',
        reason: `Commonly filtered column '${column.name}' should be indexed`,
        estimatedImpact: INDEX_IMPACT.MEDIUM,
        priority: INDEX_PRIORITY.MEDIUM,
      });
    }
  }

  return suggestions;
}

/**
 * Suggest JSONB GIN indexes
 */
export function suggestJsonbIndexes(
  table: SQLTable,
  indexedColumns: Set<string>
): IndexSuggestion[] {
  const suggestions: IndexSuggestion[] = [];

  for (const column of table.columns) {
    if (column.type === 'JSONB' && !indexedColumns.has(column.name)) {
      suggestions.push({
        table: table.name,
        columns: [column.name],
        type: 'GIN',
        reason: `JSONB column '${column.name}' should have a GIN index for efficient queries`,
        estimatedImpact: INDEX_IMPACT.HIGH,
        priority: INDEX_PRIORITY.HIGH,
      });
    }
  }

  return suggestions;
}

/**
 * Suggest text search indexes
 */
export function suggestTextSearchIndexes(
  table: SQLTable,
  indexedColumns: Set<string>
): IndexSuggestion[] {
  const suggestions: IndexSuggestion[] = [];

  for (const column of table.columns) {
    if (
      column.type === 'TEXT' &&
      column.name.includes('description') &&
      !indexedColumns.has(column.name)
    ) {
      suggestions.push({
        table: table.name,
        columns: [column.name],
        type: 'GIN',
        reason: `Text column '${column.name}' may benefit from full-text search index`,
        estimatedImpact: INDEX_IMPACT.MEDIUM,
        priority: INDEX_PRIORITY.LOW,
      });
    }
  }

  return suggestions;
}

/**
 * Suggest compound indexes for common query patterns
 */
export function suggestCompoundIndexes(table: SQLTable, existingIndexes: any[]): IndexSuggestion[] {
  const suggestions: IndexSuggestion[] = [];

  const timestampColumns = table.columns.filter(
    c => c.name === 'created_at' || c.name === 'updated_at'
  );
  const statusColumn = table.columns.find(c => c.name === 'status');

  if (statusColumn && timestampColumns.length > 0) {
    const compoundCols = [statusColumn.name, timestampColumns[0].name];
    const hasCompoundIndex = existingIndexes.some(
      idx => idx.columns.length > 1 && idx.columns.includes(statusColumn.name)
    );

    if (!hasCompoundIndex) {
      suggestions.push({
        table: table.name,
        columns: compoundCols,
        type: 'BTREE',
        reason: `Compound index on status and timestamp for efficient filtering and sorting`,
        estimatedImpact: INDEX_IMPACT.HIGH,
        priority: INDEX_PRIORITY.HIGH,
      });
    }
  }

  return suggestions;
}

/**
 * Normalization Helper Functions
