        affectedTables: [table.name],
        proposedChanges: `Consider extracting columns that depend on only part of the composite key`,
        normalForm: '2NF',
      });
    }
  }

  return suggestions;
}

/**
 * Check for transitive dependencies (3NF violation)
 */
export function detectTransitiveDependencies(table: SQLTable): NormalizationSuggestion[] {
  const suggestions: NormalizationSuggestion[] = [];

  // Check for address columns that suggest transitive dependencies
  const addressColumns = table.columns.filter(
    c => c.name.includes('city') || c.name.includes('state') || c.name.includes('country')
  );

  if (addressColumns.length >= NORMALIZATION_LIMITS.MIN_ADDRESS_COLUMNS_FOR_EXTRACTION) {
    suggestions.push({
      type: 'EXTRACT_TABLE',
      description: `Table '${table.name}' has potential transitive dependencies in address columns`,
      affectedTables: [table.name],
      proposedChanges: `Extract address fields into a separate 'addresses' table`,
      normalForm: '3NF',
    });
  }

  return suggestions;
}

/**
 * Check for redundant data in JSON columns
 */
export function detectRedundantData(table: SQLTable): NormalizationSuggestion[] {
  const suggestions: NormalizationSuggestion[] = [];
  const jsonColumns = table.columns.filter(c => c.type === 'JSON' || c.type === 'JSONB');

  if (jsonColumns.length > 0) {
    suggestions.push({
      type: 'REMOVE_REDUNDANCY',
      description: `Table '${table.name}' uses JSON columns which may contain redundant data`,
      affectedTables: [table.name],
      proposedChanges: `Consider normalizing JSON data into separate tables for better querying and integrity`,
      normalForm: '3NF',
    });
  }

  return suggestions;
}

/**
 * Check for missing junction tables in many-to-many relationships
 */
export function detectMissingJunctionTables(
  relationships: Relationship[]
): NormalizationSuggestion[] {
  const suggestions: NormalizationSuggestion[] = [];
  const manyToManyRels = relationships.filter(r => r.type === 'MANY_TO_MANY');

  for (const rel of manyToManyRels) {
    if (!rel.junctionTable) {
      suggestions.push({
        type: 'ADD_JUNCTION',
        description: `Many-to-many relationship '${rel.name}' needs a junction table`,
        affectedTables: [rel.from.table, rel.to.table],
        proposedChanges: `Create junction table '${rel.from.table}_${rel.to.table}' to properly model the relationship`,
        normalForm: '3NF',
      });
    }
  }

  return suggestions;
}

#!/usr/bin/env node

/**
 * Database Schema Designer MCP Server
 * Provides comprehensive database schema design, migration, and optimization tools
 */

import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import { CallToolRequestSchema, ListToolsRequestSchema } from '@modelcontextprotocol/sdk/types.js';

import { MCPError, getErrorMessage } from '@j0kz/shared';

import {
  designSchema,
  generateMigration,
  createERDiagram,
  optimizeIndexes,
  normalizeSchema,
  generateSeedData,
  validateSchema,
  analyzeSchema,
} from './designer.js';

import type { DatabaseSchema, SchemaDesignOptions, ERDiagramOptions } from './types.js';

/**
 * MCP Server for Database Schema Designer
 */
class DatabaseSchemaServer {
  private server: Server;

  constructor() {
    this.server = new Server(
      {
        name: 'db-schema-designer',
        version: '1.0.0',
      },
      {
        capabilities: {
          tools: {},
        },
      }
    );

    this.setupToolHandlers();
    this.setupErrorHandling();
  }

  /**
   * Setup tool request handlers
   */
  private setupToolHandlers(): void {
    // List available tools
    this.server.setRequestHandler(ListToolsRequestSchema, async () => ({
      tools: [
        {
          name: 'design_schema',
          description:
            'Design a database schema from plain text requirements. Supports PostgreSQL, MySQL, and MongoDB. Automatically extracts entities, relationships, and generates complete schema definitions.',
          inputSchema: {
            type: 'object',
            properties: {
              requirements: {
                type: 'string',
                description:
                  'Plain text description of data requirements (e.g., "Users have many orders. Orders contain products. Products belong to categories.")',
              },
              options: {
                type: 'object',
                description: 'Schema design options',
                properties: {
                  database: {
                    type: 'string',
                    enum: ['postgres', 'mysql', 'mongodb'],
                    description: 'Target database type',
                  },
                  normalForm: {
                    type: 'string',
                    enum: ['1NF', '2NF', '3NF', 'BCNF'],
                    description: 'Target normal form (default: 3NF)',
                  },
                  includeTimestamps: {
                    type: 'boolean',
                    description: 'Add created_at/updated_at columns (default: true)',
                  },
                  includeSoftDeletes: {
                    type: 'boolean',
                    description: 'Add deleted_at column (default: false)',
                  },
                  useUUIDs: {
                    type: 'boolean',
                    description: 'Use UUIDs instead of integers for primary keys (default: false)',
                  },
                  addIndexes: {
                    type: 'boolean',
                    description: 'Automatically add common indexes (default: true)',
                  },
                  addComments: {
                    type: 'boolean',
                    description: 'Add comments to tables and columns (default: true)',
                  },
                },
                required: ['database'],
              },
            },
            required: ['requirements', 'options'],
          },
        },
        {
          name: 'generate_migration',
          description:
            'Generate database migration files (up/down) from a schema definition. Supports SQL migrations for PostgreSQL/MySQL and mongosh commands for MongoDB.',
          inputSchema: {
            type: 'object',
            properties: {
              schema: {
                type: 'object',
                description: 'Complete database schema object',
              },
              description: {
                type: 'string',
                description: 'Migration description (e.g., "create initial schema")',
              },
            },
            required: ['schema', 'description'],
          },
        },
        {
          name: 'create_er_diagram',
          description:
            'Create an Entity-Relationship diagram from a schema. Supports Mermaid, PlantUML, and DBML formats. Perfect for documentation and visualization.',
          inputSchema: {
            type: 'object',
            properties: {
              schema: {
                type: 'object',
                description: 'Complete database schema object',
              },
              options: {
                type: 'object',
                description: 'Diagram generation options',
                properties: {
                  format: {
                    type: 'string',
                    enum: ['mermaid', 'plantuml', 'dbml'],
                    description: 'Output format (default: mermaid)',
                  },
                  includeColumns: {
                    type: 'boolean',
                    description: 'Include column details (default: true)',
                  },
                  includeIndexes: {
                    type: 'boolean',
                    description: 'Include index information (default: false)',
                  },
                  includeRelationships: {
                    type: 'boolean',
                    description: 'Show relationships between tables (default: true)',
                  },
                  theme: {
                    type: 'string',
                    enum: ['default', 'dark', 'neutral'],
                    description: 'Diagram theme (default: default)',
                  },
                },
              },
            },
            required: ['schema'],
          },
        },
        {
          name: 'optimize_indexes',
          description:
            'Analyze schema and suggest index optimizations. Identifies missing indexes on foreign keys, frequently filtered columns, and opportunities for compound indexes.',
          inputSchema: {
            type: 'object',
            properties: {
              schema: {
                type: 'object',
                description: 'Complete database schema object',
              },
            },
            required: ['schema'],
          },
        },
        {
          name: 'normalize_schema',
          description:
            'Suggest schema normalizations to improve data integrity and reduce redundancy. Identifies violations of 1NF, 2NF, 3NF, and BCNF with specific recommendations.',
          inputSchema: {
            type: 'object',
            properties: {
              schema: {
                type: 'object',
                description: 'Complete database schema object',
              },
            },
            required: ['schema'],
          },
        },
        {
          name: 'generate_seed_data',
          description:
            'Generate realistic seed data for testing. Creates mock records that respect foreign key constraints and data types.',
          inputSchema: {
            type: 'object',
            properties: {
              schema: {
                type: 'object',
                description: 'Complete database schema object',
              },
              recordsPerTable: {
                type: 'number',
                description: 'Number of records to generate per table (default: 10)',
              },
            },
            required: ['schema'],
          },
        },
        {
          name: 'validate_schema',
          description:
            'Validate schema for errors and best practice violations. Checks for missing primary keys, naming conventions, proper indexing, and more.',
          inputSchema: {
            type: 'object',
            properties: {
              schema: {
                type: 'object',
                description: 'Complete database schema object',
              },
            },
            required: ['schema'],
          },
        },
        {
          name: 'analyze_schema',
          description:
            'Analyze schema complexity and characteristics. Provides metrics on tables, columns, indexes, relationships, normal form, and estimated size.',
          inputSchema: {
            type: 'object',
            properties: {
              schema: {
                type: 'object',
                description: 'Complete database schema object',
              },
            },
            required: ['schema'],
          },
        },
      ],
    }));

    // Handle tool execution
    this.server.setRequestHandler(CallToolRequestSchema, async request => {
      try {
        const { name, arguments: args } = request.params;

        switch (name) {
          case 'design_schema':
            return await this.handleDesignSchema(args);

          case 'generate_migration':
            return await this.handleGenerateMigration(args);

          case 'create_er_diagram':
            return await this.handleCreateERDiagram(args);

          case 'optimize_indexes':
            return await this.handleOptimizeIndexes(args);

          case 'normalize_schema':
            return await this.handleNormalizeSchema(args);

          case 'generate_seed_data':
            return await this.handleGenerateSeedData(args);

          case 'validate_schema':
            return await this.handleValidateSchema(args);

          case 'analyze_schema':
            return await this.handleAnalyzeSchema(args);

          default:
            throw new MCPError('DB_009', { tool: name });
        }
      } catch (error) {
        const errorMessage = getErrorMessage(error);
        const errorCode = error instanceof MCPError ? error.code : 'UNKNOWN';

        return {
          content: [
            {
              type: 'text',
              text: JSON.stringify(
                {
                  success: false,
                  error: errorMessage,
                  code: errorCode,
                  ...(error instanceof MCPError && error.details ? { details: error.details } : {}),
                },
                null,
                2
              ),
            },
          ],
          isError: true,
        };
      }
    });
  }

  /**
   * Handle design_schema tool
   */
  private async handleDesignSchema(args: any) {
    const { requirements, options } = args as {
      requirements: string;
      options: SchemaDesignOptions;
    };

    if (!requirements || !options?.database) {
      throw new MCPError('DB_001');
    }

    const schema = designSchema(requirements, options);

    return {
      content: [
        {
          type: 'text',
          text: JSON.stringify(schema, null, 2),
        },
      ],
    };
  }

  /**
   * Handle generate_migration tool
   */
  private async handleGenerateMigration(args: any) {
    const { schema, description } = args as {
      schema: DatabaseSchema;
      description: string;
    };

    if (!schema || !description) {
      throw new MCPError('DB_002');
    }

    const migration = generateMigration(schema, description);

    return {
      content: [
        {
          type: 'text',
          text: JSON.stringify(migration, null, 2),
        },
      ],
    };
  }

  /**
   * Handle create_er_diagram tool
   */
  private async handleCreateERDiagram(args: any) {
    const {
      schema,
      options = { format: 'mermaid', includeColumns: true, includeRelationships: true },
    } = args as {
      schema: DatabaseSchema;
      options?: ERDiagramOptions;
    };

    if (!schema) {
      throw new MCPError('DB_003');
    }

    const diagram = createERDiagram(schema, options);

    return {
      content: [
        {
          type: 'text',
          text: diagram,
        },
      ],
    };
  }

  /**
   * Handle optimize_indexes tool
   */
  private async handleOptimizeIndexes(args: any) {
    const { schema } = args as { schema: DatabaseSchema };

    if (!schema) {
      throw new MCPError('DB_003');
    }

    const suggestions = optimizeIndexes(schema);

    return {
      content: [
        {
          type: 'text',
          text: JSON.stringify(suggestions, null, 2),
        },
      ],
    };
  }

  /**
   * Handle normalize_schema tool
   */
  private async handleNormalizeSchema(args: any) {
    const { schema } = args as { schema: DatabaseSchema };

    if (!schema) {
      throw new MCPError('DB_003');
    }

    const suggestions = normalizeSchema(schema);

    return {
      content: [
        {
          type: 'text',
          text: JSON.stringify(suggestions, null, 2),
        },
      ],
    };
  }

  /**
   * Handle generate_seed_data tool
   */
  private async handleGenerateSeedData(args: any) {
    const { schema, recordsPerTable = 10 } = args as {
      schema: DatabaseSchema;
      recordsPerTable?: number;
    };

    if (!schema) {
      throw new MCPError('DB_003');
    }

    const seedData = generateSeedData(schema, recordsPerTable);

    return {
      content: [
        {
          type: 'text',
          text: JSON.stringify(seedData, null, 2),
        },
      ],
    };
  }

  /**
   * Handle validate_schema tool
   */
  private async handleValidateSchema(args: any) {
    const { schema } = args as { schema: DatabaseSchema };

    if (!schema) {
      throw new MCPError('DB_003');
    }

    const validation = validateSchema(schema);

    return {
      content: [
        {
          type: 'text',
          text: JSON.stringify(validation, null, 2),
        },
      ],
    };
  }

  /**
   * Handle analyze_schema tool
   */
  private async handleAnalyzeSchema(args: any) {
    const { schema } = args as { schema: DatabaseSchema };

    if (!schema) {
      throw new MCPError('DB_003');
    }

    const analysis = analyzeSchema(schema);

    return {
      content: [
        {
          type: 'text',
          text: JSON.stringify(analysis, null, 2),
        },
      ],
    };
  }

  /**
   * Setup error handling
   */
  private setupErrorHandling(): void {
    this.server.onerror = error => {
      console.error('[MCP Error]', error);
    };

    process.on('SIGINT', async () => {
      await this.server.close();
      process.exit(0);
    });
  }

  /**
   * Start the MCP server
   */
  async run(): Promise<void> {
    const transport = new StdioServerTransport();
    await this.server.connect(transport);
    console.error('Database Schema Designer MCP server running on stdio');
  }
}

// Start the server
const server = new DatabaseSchemaServer();
server.run().catch(console.error);

/**
 * Database Schema Designer Types
 * Comprehensive type definitions for multi-database schema design
 */

/**
 * Supported database types
 */
export type DatabaseType = 'postgres' | 'mysql' | 'mongodb';

/**
 * SQL column data types
 */
export type SQLDataType =
  | 'INTEGER'
  | 'BIGINT'
  | 'SMALLINT'
  | 'SERIAL'
  | 'BIGSERIAL'
  | 'VARCHAR'
  | 'TEXT'
  | 'CHAR'
  | 'BOOLEAN'
  | 'DATE'
  | 'TIMESTAMP'
  | 'TIMESTAMPTZ'
  | 'TIME'
  | 'DECIMAL'
  | 'NUMERIC'
  | 'FLOAT'
  | 'DOUBLE'
  | 'REAL'
  | 'JSON'
  | 'JSONB'
  | 'UUID'
  | 'BYTEA'
  | 'ARRAY'
  | 'ENUM';

/**
 * MongoDB field types
 */
export type MongoDataType =
  | 'String'
  | 'Number'
  | 'Boolean'
  | 'Date'
  | 'ObjectId'
  | 'Array'
  | 'Object'
  | 'Mixed'
  | 'Buffer'
  | 'Decimal128'
  | 'Map';

/**
 * Column constraint types
 */
export type ConstraintType =
  | 'PRIMARY_KEY'
  | 'FOREIGN_KEY'
  | 'UNIQUE'
  | 'NOT_NULL'
  | 'CHECK'
  | 'DEFAULT';

/**
 * Index types
 */
export type IndexType = 'BTREE' | 'HASH' | 'GIN' | 'GIST' | 'BRIN' | 'COMPOUND' | 'TEXT';

/**
 * Relationship types
 */
export type RelationType = 'ONE_TO_ONE' | 'ONE_TO_MANY' | 'MANY_TO_MANY';

/**
 * Column definition for SQL databases
 */
export interface SQLColumn {
  name: string;
  type: SQLDataType;
  length?: number;
  precision?: number;
  scale?: number;
  nullable?: boolean;
  defaultValue?: string | number | boolean;
  autoIncrement?: boolean;
  unique?: boolean;
  primaryKey?: boolean;
  comment?: string;
  enumValues?: string[];
}

/**
 * Field definition for MongoDB
 */
export interface MongoField {
  name: string;
  type: MongoDataType;
  required?: boolean;
  unique?: boolean;
  default?: any;
  enum?: any[];
  min?: number;
  max?: number;
  minlength?: number;
  maxlength?: number;
  match?: string;
  validate?: string;
  ref?: string;
  index?: boolean;
  sparse?: boolean;
}

/**
 * Foreign key constraint
 */
export interface ForeignKey {
  name: string;
  column: string;
  referencedTable: string;
  referencedColumn: string;
  onDelete?: 'CASCADE' | 'SET_NULL' | 'RESTRICT' | 'NO_ACTION';
  onUpdate?: 'CASCADE' | 'SET_NULL' | 'RESTRICT' | 'NO_ACTION';
}

/**
 * Index definition
 */
export interface Index {
  name: string;
  columns: string[];
  type?: IndexType;
  unique?: boolean;
  where?: string;
  comment?: string;
}

/**
 * Check constraint
 */
export interface CheckConstraint {
  name: string;
  expression: string;
}

/**
 * SQL table definition
 */
export interface SQLTable {
  name: string;
  columns: SQLColumn[];
  primaryKey?: string | string[];
  foreignKeys?: ForeignKey[];
  indexes?: Index[];
  checkConstraints?: CheckConstraint[];
  comment?: string;
}

/**
 * MongoDB collection definition
 */
export interface MongoCollection {
  name: string;
  fields: MongoField[];
  indexes?: Index[];
  validationLevel?: 'strict' | 'moderate' | 'off';
  validationAction?: 'error' | 'warn';
  timeseries?: {
    timeField: string;
    metaField?: string;
    granularity?: 'seconds' | 'minutes' | 'hours';
  };
  capped?: {
    size: number;
    max?: number;
  };
  comment?: string;
}

/**
 * Relationship between tables/collections
 */
export interface Relationship {
  name: string;
  type: RelationType;
  from: {
    table: string;
    column: string;
  };
  to: {
    table: string;
    column: string;
  };
  junctionTable?: string;
}

/**
 * Complete database schema
 */
export interface DatabaseSchema {
  database: DatabaseType;
  name: string;
  version?: string;
  tables?: SQLTable[];
  collections?: MongoCollection[];
  relationships?: Relationship[];
  metadata?: {
    author?: string;
    description?: string;
    createdAt?: string;
    updatedAt?: string;
  };
}

/**
 * Schema design options
 */
export interface SchemaDesignOptions {
  database: DatabaseType;
  normalForm?: '1NF' | '2NF' | '3NF' | 'BCNF';
  includeTimestamps?: boolean;
  includeSoftDeletes?: boolean;
  useUUIDs?: boolean;
  addIndexes?: boolean;
  addComments?: boolean;
}

/**
 * Migration file format
 */
export interface Migration {
  version: string;
  description: string;
  up: string;
  down: string;
  timestamp: string;
}

/**
 * Index optimization suggestion
 */
export interface IndexSuggestion {
  table: string;
  columns: string[];
  type: IndexType;
  reason: string;
  estimatedImpact: 'HIGH' | 'MEDIUM' | 'LOW';
  priority: number;
}

/**
 * Normalization suggestion
 */
export interface NormalizationSuggestion {
  type: 'EXTRACT_TABLE' | 'MERGE_COLUMNS' | 'REMOVE_REDUNDANCY' | 'ADD_JUNCTION';
  description: string;
  affectedTables: string[];
  proposedChanges: string;
  normalForm: '1NF' | '2NF' | '3NF' | 'BCNF';
}

/**
 * Seed data for a table/collection
 */
export interface SeedData {
  table: string;
  records: Record<string, any>[];
  truncateFirst?: boolean;
}

/**
 * Schema validation result
 */
export interface ValidationResult {
  valid: boolean;
  errors: ValidationError[];
  warnings: ValidationWarning[];
}

/**
 * Validation error
 */
export interface ValidationError {
  type:
    | 'MISSING_PRIMARY_KEY'
    | 'INVALID_FOREIGN_KEY'
    | 'DUPLICATE_COLUMN'
    | 'INVALID_TYPE'
    | 'NAMING_VIOLATION';
  table: string;
  column?: string;
  message: string;
  severity: 'ERROR' | 'CRITICAL';
}

/**
 * Validation warning
 */
export interface ValidationWarning {
  type: 'NO_INDEX' | 'LONG_VARCHAR' | 'NULLABLE_FOREIGN_KEY' | 'MISSING_TIMESTAMP' | 'DENORMALIZED';
  table: string;
  column?: string;
  message: string;
  suggestion?: string;
}

/**
 * ER diagram options
 */
export interface ERDiagramOptions {
  format: 'mermaid' | 'plantuml' | 'dbml';
  includeColumns?: boolean;
  includeIndexes?: boolean;
  includeRelationships?: boolean;
  theme?: 'default' | 'dark' | 'neutral';
}

/**
 * Schema analysis result
 */
export interface SchemaAnalysis {
  tableCount: number;
  columnCount: number;
  indexCount: number;
  relationshipCount: number;
  normalForm: '1NF' | '2NF' | '3NF' | 'BCNF' | 'DENORMALIZED';
  estimatedSize: {
    rows: number;
    storage: string;
  };
  complexity: 'LOW' | 'MEDIUM' | 'HIGH';
}

