        findingsBySeverity[SeverityLevel.LOW] * SEVERITY_WEIGHTS.LOW +
        findingsBySeverity[SeverityLevel.INFO] * SEVERITY_WEIGHTS.INFO;
    const securityScore = Math.max(SECURITY_SCORE.MIN, Math.min(SECURITY_SCORE.MAX, SECURITY_SCORE.MAX - totalWeight));
    return {
        totalFindings: findings.length,
        findingsBySeverity,
        findingsByType,
        findings,
        dependencyVulnerabilities,
        filesScanned,
        scanDuration,
        config,
        timestamp: new Date(),
        securityScore,
    };
