 */
const server = new Server(
  {
    name: 'security-scanner',
    version: '1.0.0',
  },
  {
    capabilities: {
      tools: {},
    },
  }
);

/**
 * List available tools
 */
server.setRequestHandler(ListToolsRequestSchema, async () => ({
  tools: TOOLS,
}));

/**
 * Handle tool calls
 */
server.setRequestHandler(CallToolRequestSchema, async request => {
  const { name, arguments: args } = request.params;

  try {
    switch (name) {
      case 'scan_file': {
        const { filePath, config } = args as { filePath: string; config?: ScanConfig };
        const validatedPath = validateFilePath(filePath);
        const findings = await scanFile(validatedPath, config);

        return {
          content: [
            {
              type: 'text',
              text: JSON.stringify(
                {
                  success: true,
                  filePath,
                  findingsCount: findings.length,
                  findings,
                },
                null,
                2
              ),
            },
          ],
        };
      }

      case 'scan_project': {
        const { projectPath, config } = args as { projectPath: string; config?: ScanConfig };
        const validatedPath = validateDirectoryPath(projectPath);
        const results = await scanProject(validatedPath, config);

        return {
          content: [
            {
              type: 'text',
              text: JSON.stringify(
                {
                  success: true,
                  ...results,
                },
                null,
                2
              ),
            },
          ],
        };
      }

      case 'scan_secrets': {
        const { targetPath, customPatterns } = args as {
          targetPath: string;
          customPatterns?: any[];
        };

        const validatedPath = validatePath(targetPath);
        const stats = fs.statSync(validatedPath);
        let findings: SecurityFinding[] = [];

        if (stats.isFile()) {
          const config: ScanConfig = {
            scanSecrets: true,
            scanSQLInjection: false,
            scanXSS: false,
            scanOWASP: false,
            scanDependencies: false,
          };
          findings = await scanFile(validatedPath, config);
        } else {
          const config: ScanConfig = {
            scanSecrets: true,
            scanSQLInjection: false,
            scanXSS: false,
            scanOWASP: false,
            scanDependencies: false,
            customPatterns,
          };
          const results = await scanProject(validatedPath, config);
          findings = results.findings;
        }

        return {
          content: [
            {
              type: 'text',
              text: JSON.stringify(
                {
                  success: true,
                  secretsFound: findings.length,
                  findings,
                },
                null,
                2
              ),
            },
          ],
        };
      }

      case 'scan_vulnerabilities': {
        const { targetPath, vulnerabilityTypes } = args as {
          targetPath: string;
          vulnerabilityTypes: string[];
        };

        const validatedPath = validatePath(targetPath);

        const config: ScanConfig = {
          scanSecrets: false,
          scanSQLInjection: vulnerabilityTypes.includes('sql_injection'),
          scanXSS: vulnerabilityTypes.includes('xss'),
          scanOWASP:
            vulnerabilityTypes.includes('weak_crypto') ||
            vulnerabilityTypes.includes('insecure_deserialization') ||
            vulnerabilityTypes.includes('path_traversal'),
          scanDependencies: false,
        };

        const stats = fs.statSync(validatedPath);
        let findings: SecurityFinding[] = [];

        if (stats.isFile()) {
          findings = await scanFile(validatedPath, config);
        } else {
          const results = await scanProject(validatedPath, config);
          findings = results.findings;
        }

        // Filter findings by requested types
        findings = findings.filter(f => vulnerabilityTypes.includes(f.type));

        return {
          content: [
            {
              type: 'text',
              text: JSON.stringify(
                {
                  success: true,
                  vulnerabilityTypes,
                  findingsCount: findings.length,
                  findings,
                },
                null,
                2
              ),
            },
          ],
        };
      }

      case 'generate_security_report': {
        const { projectPath, outputPath, config } = args as {
          projectPath: string;
          outputPath?: string;
          config?: ScanConfig;
        };

        const validatedProjectPath = validateDirectoryPath(projectPath);
        const results = await scanProject(validatedProjectPath, config);
        const report = generateSecurityReport(results);
        const markdown = formatReportAsMarkdown(report);

        // Save to file if output path provided
        if (outputPath) {
          const validatedOutputPath = validatePath(outputPath);
          fs.writeFileSync(validatedOutputPath, markdown, 'utf-8');
        }

        return {
          content: [
            {
              type: 'text',
              text: markdown,
            },
          ],
        };
      }

      default:
        throw new MCPError('SEC_004', { tool: name });
    }
  } catch (error) {
    const errorMessage = getErrorMessage(error);
    const errorCode = error instanceof MCPError ? error.code : 'UNKNOWN';

    return {
      content: [
        {
          type: 'text',
          text: JSON.stringify(
            {
              success: false,
              error: errorMessage,
              code: errorCode,
              ...(error instanceof MCPError && error.details ? { details: error.details } : {}),
            },
            null,
            2
          ),
        },
      ],
      isError: true,
    };
  }
});

/**
 * Start the server
 */
async function main() {
  const transport = new StdioServerTransport();
  await server.connect(transport);
  console.error('Security Scanner MCP server running on stdio');
}

main().catch(error => {
  console.error('Fatal error:', error);
  process.exit(1);
});

/**
 * OWASP Top 10 Security Scanner Module
 * Detects common security vulnerabilities from OWASP Top 10
 */

import { readFileSync } from 'fs';
import { join, dirname } from 'path';
import { fileURLToPath } from 'url';
import {
  SecurityFinding,
  FileScanContext,
  SeverityLevel,
  VulnerabilityType,
  OWASPCategory,
} from '../types.js';
import { DEPENDENCY_LIMITS, CWE_IDS, CVSS_SCORES } from '../constants/security-thresholds.js';
import { generateFindingId, extractCodeContext, isScannerFile } from '../utils.js';

const __dirname = dirname(fileURLToPath(import.meta.url));

/**
 * Perform OWASP Top 10 security checks
 */
export async function scanOWASP(context: FileScanContext): Promise<SecurityFinding[]> {
  const findings: SecurityFinding[] = [];
  const lines = context.content.split('\n');

  // Skip scanner's own files to avoid false positives
  if (isScannerFile(context.filePath)) {
    return findings;
  }

  // Load patterns from external JSON to avoid CodeQL false positives
  const cryptoPatternsPath = join(__dirname, '../patterns', 'crypto-patterns.json');
  const cryptoPatternsData = JSON.parse(readFileSync(cryptoPatternsPath, 'utf-8'));

  const weakCryptoPatterns = cryptoPatternsData.weakAlgorithms.map((p: any) => ({
    pattern: new RegExp(p.pattern, 'gi'),
    algo: p.algo,
  }));

  for (const { pattern, algo } of weakCryptoPatterns) {
    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];

      if (pattern.test(line)) {
        const codeContext = extractCodeContext(context.content, i + 1);

        findings.push({
          id: generateFindingId(context.filePath, i + 1, VulnerabilityType.WEAK_CRYPTO),
          type: VulnerabilityType.WEAK_CRYPTO,
          severity: SeverityLevel.MEDIUM,
          title: `Weak Cryptographic Algorithm: ${algo}`,
          description: `Usage of ${algo} detected. This algorithm is cryptographically broken and should not be used.`,
          filePath: context.filePath,
          line: i + 1,
          codeSnippet: codeContext.issueLine,
          recommendation:
            'Use strong cryptographic algorithms like SHA-256, SHA-384, or SHA-512 for hashing. Use AES-256-GCM for encryption.',
          owaspCategory: OWASPCategory.A02_CRYPTOGRAPHIC_FAILURES,
          cweId: CWE_IDS.WEAK_CRYPTO,
          metadata: { algorithm: algo },
        });
      }
    }
  }

  // Check for insecure deserialization
  const deserializationPatterns = [
    /pickle\.loads?\(/gi,
    /yaml\.load\(/gi,
    /unserialize\(/gi,
    /JSON\.parse\(.{0,200}?(?:localStorage|sessionStorage|location\.)/gi,
  ];

  for (const pattern of deserializationPatterns) {
