/**
 * Caching system for MCP tools
 * Improves performance by caching expensive operations
 */

import { LRUCache } from 'lru-cache';
import { CacheEntry } from '../types/index.js';
import { generateHash } from '../utils/index.js';
import crypto from 'crypto';

// Cache configuration constants
const DEFAULT_MAX_CACHE_SIZE = 1000;
const DEFAULT_TTL_MS = 3600000; // 1 hour
const DEFAULT_FILE_CACHE_SIZE = 500;
const DEFAULT_ANALYSIS_CACHE_SIZE = 200;
const DEFAULT_ANALYSIS_TTL_MS = 1800000; // 30 minutes
const PERCENTAGE_MULTIPLIER = 100;

/**
 * Generic cache interface
 */
export interface ICache<T = any> {
  get(key: string): T | undefined;
  set(key: string, value: T, ttl?: number): void;
  has(key: string): boolean;
  delete(key: string): boolean;
  clear(): void;
  size(): number;
}

/**
 * Memory cache with LRU eviction
 */
export class MemoryCache<T = any> implements ICache<T> {
  private cache: LRUCache<string, CacheEntry<T>>;
  private hits: number = 0;
  private misses: number = 0;

  constructor(options: { max?: number; ttl?: number } = {}) {
    const maxSize = options.max ?? DEFAULT_MAX_CACHE_SIZE;
    const ttl = options.ttl ?? DEFAULT_TTL_MS;

    this.cache = new LRUCache<string, CacheEntry<T>>({
      max: maxSize,
      ttl: ttl,
      updateAgeOnGet: true,
    });
  }

  /**
   * Get value from cache
   */
  get(key: string): T | undefined {
    const entry = this.cache.get(key);

    if (entry) {
      this.hits++;
      entry.hits++;
      return entry.value;
    }

    this.misses++;
    return undefined;
  }

  /**
   * Set value in cache
   */
  set(key: string, value: T, ttl?: number): void {
    const entry: CacheEntry<T> = {
      key,
      value,
      timestamp: Date.now(),
      ttl: ttl || DEFAULT_TTL_MS,
      hits: 0,
    };

    this.cache.set(key, entry, { ttl: entry.ttl });
  }

  /**
   * Check if key exists in cache
   */
  has(key: string): boolean {
    return this.cache.has(key);
  }

  /**
   * Delete value from cache
   */
  delete(key: string): boolean {
    return this.cache.delete(key);
  }

  /**
   * Clear all cache
   */
  clear(): void {
    this.cache.clear();
    this.hits = 0;
    this.misses = 0;
  }

  /**
   * Get cache size
   */
  size(): number {
    return this.cache.size;
  }

  /**
   * Get cache statistics
   */
  getStats(): {
    hits: number;
    misses: number;
    hitRate: number;
    size: number;
    maxSize: number;
  } {
    const total = this.hits + this.misses;
    return {
      hits: this.hits,
      misses: this.misses,
      hitRate: total > 0 ? (this.hits / total) * PERCENTAGE_MULTIPLIER : 0,
      size: this.cache.size,
      maxSize: this.cache.max,
    };
  }

  /**
   * Get all keys
   */
  keys(): string[] {
    return Array.from(this.cache.keys());
  }

  /**
   * Get all entries
   */
  entries(): Array<[string, T]> {
    return Array.from(this.cache.entries()).map(([key, entry]) => [key, entry.value]);
  }
}

/**
 * File content cache with hash-based keys
 */
export class FileCache {
  private cache: MemoryCache<{ content: string; hash: string }>;

  constructor(maxSize: number = DEFAULT_FILE_CACHE_SIZE) {
    this.cache = new MemoryCache({ max: maxSize });
  }

  /**
   * Get cached file content
   */
  get(filePath: string, currentHash: string): string | undefined {
    const cached = this.cache.get(filePath);

    if (cached && cached.hash === currentHash) {
      return cached.content;
    }

    // Hash mismatch or not cached
    if (cached) {
      this.cache.delete(filePath);
    }

    return undefined;
  }

  /**
   * Cache file content
   */
  set(filePath: string, content: string): void {
    const hash = generateHash(content);
    this.cache.set(filePath, { content, hash });
  }

  /**
   * Check if file is cached with matching hash
   */
  has(filePath: string, currentHash: string): boolean {
    const cached = this.cache.get(filePath);
    return !!cached && cached.hash === currentHash;
  }

  /**
   * Invalidate cache for file
   */
  invalidate(filePath: string): boolean {
    return this.cache.delete(filePath);
  }

  /**
   * Clear all file cache
   */
  clear(): void {
    this.cache.clear();
  }

  /**
   * Get cache statistics
   */
  getStats() {
    return this.cache.getStats();
  }
}

/**
 * Analysis result cache
 */
export class AnalysisCache {
  private cache: MemoryCache<any>;

  constructor(
    maxSize: number = DEFAULT_ANALYSIS_CACHE_SIZE,
    ttl: number = DEFAULT_ANALYSIS_TTL_MS
  ) {
    this.cache = new MemoryCache({ max: maxSize, ttl });
  }

  /**
   * Generate cache key for analysis
   */
  private generateKey(
    filePath: string,
    analysisType: string,
    fileHash: string,
    config?: any
  ): string {
    const configHash = config ? generateHash(JSON.stringify(config)) : '';
    return `${analysisType}:${filePath}:${fileHash}:${configHash}`;
  }

  /**
   * Get cached analysis result
   */
  get(filePath: string, analysisType: string, fileHash: string, config?: any): any | undefined {
    const key = this.generateKey(filePath, analysisType, fileHash, config);
    return this.cache.get(key);
  }

  /**
   * Cache analysis result
   */
  set(filePath: string, analysisType: string, fileHash: string, result: any, config?: any): void {
    const key = this.generateKey(filePath, analysisType, fileHash, config);
    this.cache.set(key, result);
  }

  /**
   * Check if analysis is cached
   */
  has(filePath: string, analysisType: string, fileHash: string, config?: any): boolean {
    const key = this.generateKey(filePath, analysisType, fileHash, config);
    return this.cache.has(key);
  }

  /**
   * Invalidate cache for file
   */
  invalidate(filePath: string): void {
    // Remove all entries for this file
    for (const key of this.cache.keys()) {
      if (key.includes(filePath)) {
        this.cache.delete(key);
      }
    }
  }

  /**
   * Clear all analysis cache
   */
  clear(): void {
    this.cache.clear();
  }

  /**
   * Get cache statistics
   */
  getStats() {
    return this.cache.getStats();
  }
}

/**
 * Global cache manager
 */
export class CacheManager {
  private caches: Map<string, ICache> = new Map();

  /**
   * Register a cache
   */
  register<T>(name: string, cache: ICache<T>): void {
    this.caches.set(name, cache);
  }

  /**
   * Get a cache by name
   */
  get<T>(name: string): ICache<T> | undefined {
    return this.caches.get(name) as ICache<T> | undefined;
  }

  /**
   * Clear all caches
   */
  clearAll(): void {
    for (const cache of this.caches.values()) {
      cache.clear();
    }
  }

  /**
   * Get statistics for all caches
   */
  getAllStats(): Record<string, any> {
    const stats: Record<string, any> = {};

    for (const [name, cache] of this.caches.entries()) {
      stats[name] = {
        size: cache.size(),
        ...(cache instanceof MemoryCache ? cache.getStats() : {}),
      };
    }

    return stats;
  }

  /**
   * Remove a cache
   */
  remove(name: string): boolean {
    const cache = this.caches.get(name);
    if (cache) {
      cache.clear();
      return this.caches.delete(name);
    }
    return false;
  }
}

/**
 * Decorator for caching function results
 */
export function cached(options: { ttl?: number; keyGenerator?: (...args: any[]) => string } = {}) {
  const cache = new MemoryCache({ ttl: options.ttl });

  return function (target: any, propertyKey: string, descriptor: PropertyDescriptor) {
    const originalMethod = descriptor.value;

    descriptor.value = async function (...args: any[]) {
      let key: string;
      if (options.keyGenerator) {
        key = options.keyGenerator(...args);
      } else {
        key = crypto.createHash('sha256').update(JSON.stringify(args)).digest('hex');
      }

      const cached = cache.get(key);
      if (cached !== undefined) {
        return cached;
      }

      const result = await originalMethod.apply(this, args);
      cache.set(key, result);

      return result;
    };

    return descriptor;
  };
}

/**
 * Optimized file system operations for MCP tools
 * Provides caching, batching, and parallel operations
 */

import fs from 'fs/promises';
import path from 'path';
import fastGlob from 'fast-glob';
import { FileInfo } from '../types/index.js';
import { FileCache } from '../cache/index.js';
import { generateHash, normalizePath, isPathSafe } from '../utils/index.js';
import { batchProcess } from '../performance/index.js';
import type { Stats } from 'node:fs';

const { glob } = fastGlob;

/**
 * File system manager with caching and optimizations
 */
export class FileSystemManager {
  private fileCache: FileCache;
  private statsCache: Map<string, { stats: Stats; timestamp: number }> = new Map();
  private statsCacheTTL: number = 60000; // 1 minute

  constructor(cacheSize: number = 500) {
    this.fileCache = new FileCache(cacheSize);
  }

  /**
   * Read file with caching
   */
  async readFile(filePath: string, useCache: boolean = true): Promise<string> {
    const normalizedPath = normalizePath(filePath);

    if (useCache) {
      const hash = await this.getFileHash(normalizedPath);
      const cached = this.fileCache.get(normalizedPath, hash);

      if (cached) {
        return cached;
      }
    }

    const content = await fs.readFile(normalizedPath, 'utf-8');

    if (useCache) {
      this.fileCache.set(normalizedPath, content);
    }

    return content;
  }

  /**
   * Read multiple files in parallel
   */
  async readFiles(
    filePaths: string[],
    options: { useCache?: boolean; concurrency?: number } = {}
  ): Promise<Map<string, string>> {
    const { useCache = true, concurrency = 10 } = options;
    const results = new Map<string, string>();

    const contents = await batchProcess(
      filePaths,
      async filePath => {
        try {
          return await this.readFile(filePath, useCache);
        } catch {
          return null;
        }
      },
      { concurrency }
    );

    filePaths.forEach((filePath, index) => {
      if (contents[index]) {
        results.set(filePath, contents[index]!);
      }
    });

    return results;
  }

  /**
   * Get file statistics with caching
   */
  async getStats(filePath: string, useCache: boolean = true): Promise<Stats> {
    const normalizedPath = normalizePath(filePath);

    if (useCache) {
      const cached = this.statsCache.get(normalizedPath);
      if (cached && Date.now() - cached.timestamp < this.statsCacheTTL) {
        return cached.stats;
      }
    }

    const stats = await fs.stat(normalizedPath);

    if (useCache) {
      this.statsCache.set(normalizedPath, { stats, timestamp: Date.now() });
    }

    return stats;
  }

  /**
   * Get file information
   */
  async getFileInfo(filePath: string): Promise<FileInfo> {
    const normalizedPath = normalizePath(filePath);
    const stats = await this.getStats(normalizedPath);
    const content = await this.readFile(normalizedPath);

    return {
      path: normalizedPath,
      name: path.basename(normalizedPath),
      extension: path.extname(normalizedPath),
      size: stats.size,
      mtime: stats.mtime,
      content,
      hash: generateHash(content),
    };
  }

  /**
   * Find files matching patterns
   */
  async findFiles(
    patterns: string | string[],
    options: {
      cwd?: string;
      ignore?: string[];
      absolute?: boolean;
      onlyFiles?: boolean;
    } = {}
  ): Promise<string[]> {
    const files = await glob(patterns, {
      cwd: options.cwd || process.cwd(),
      ignore: options.ignore || ['node_modules/**', '.git/**'],
      absolute: options.absolute !== false,
      onlyFiles: options.onlyFiles !== false,
    });

    return files.map(normalizePath);
  }

  /**
   * Watch files for changes
   */
  async *watchFiles(
    patterns: string | string[],
    options: {
      cwd?: string;
      ignore?: string[];
    } = {}
  ): AsyncGenerator<{ event: string; path: string }> {
    const files = await this.findFiles(patterns, options);
    const watchers = new Map<string, any>();

    try {
      for (const file of files) {
        const watcher = fs.watch(file);
        watchers.set(file, watcher as any);

        (async () => {
          for await (const _event of watcher) {
            // Invalidate caches
            this.fileCache.invalidate(file);
            this.statsCache.delete(file);

            // Yield event
            // Note: This is a simplified implementation
          }
        })();
      }

      // This is a placeholder - actual implementation would be more complex
      yield { event: 'change', path: files[0] };
    } finally {
      for (const watcher of watchers.values()) {
        watcher.close();
      }
    }
  }

  /**
   * Get file hash
   */
  private async getFileHash(filePath: string): Promise<string> {
    const stats = await this.getStats(filePath);
    return `${stats.mtime.getTime()}-${stats.size}`;
  }

  /**
   * Check if file exists
   */
  async exists(filePath: string): Promise<boolean> {
    try {
      await fs.access(filePath);
      return true;
    } catch {
      return false;
    }
  }

  /**
   * Ensure directory exists
   */
  async ensureDir(dirPath: string): Promise<void> {
    await fs.mkdir(dirPath, { recursive: true });
  }

  /**
   * Write file safely
   */
  async writeFile(filePath: string, content: string, baseDir?: string): Promise<void> {
    const normalizedPath = normalizePath(filePath);

    // Security check
    if (baseDir && !isPathSafe(normalizedPath, baseDir)) {
      throw new Error('Path traversal detected');
    }

    // Ensure directory exists
    await this.ensureDir(path.dirname(normalizedPath));

    // Write file
    await fs.writeFile(normalizedPath, content, 'utf-8');

    // Update cache
    this.fileCache.set(normalizedPath, content);
  }

  /**
   * Copy file
   */
  async copyFile(src: string, dest: string): Promise<void> {
    await this.ensureDir(path.dirname(dest));
    await fs.copyFile(src, dest);
  }

  /**
   * Delete file
   */
  async deleteFile(filePath: string): Promise<void> {
    await fs.unlink(filePath);

    // Invalidate caches
    this.fileCache.invalidate(filePath);
    this.statsCache.delete(filePath);
  }

  /**
   * Get directory contents
   */
  async readDir(dirPath: string, recursive: boolean = false): Promise<string[]> {
    if (recursive) {
      return this.findFiles('**/*', { cwd: dirPath });
    }

    const entries = await fs.readdir(dirPath);
    return entries.map(entry => path.join(dirPath, entry));
  }

  /**
   * Clear all caches
   */
  clearCache(): void {
    this.fileCache.clear();
    this.statsCache.clear();
  }

  /**
   * Get cache statistics
   */
  getCacheStats() {
    return {
      fileCache: this.fileCache.getStats(),
      statsCache: {
        size: this.statsCache.size,
      },
    };
  }
}

/**
 * File watcher with debouncing
 */
export class FileWatcher {
  private watchers: Map<string, any> = new Map();
  private listeners: Map<string, Set<(event: string, filename: string) => void>> = new Map();
  private debounceTimers: Map<string, NodeJS.Timeout> = new Map();
  private debounceDelay: number;

  constructor(debounceDelay: number = 100) {
    this.debounceDelay = debounceDelay;
  }

  /**
   * Watch a file or directory
   */
  watch(
    filePath: string,
    callback: (event: string, filename: string) => void,
    options?: { recursive?: boolean }
  ): () => void {
    const normalizedPath = normalizePath(filePath);

    if (!this.watchers.has(normalizedPath)) {
      const watcher = fs.watch(normalizedPath, options || {});
      this.watchers.set(normalizedPath, watcher as any);

      (async () => {
        for await (const event of watcher) {
          this.handleEvent(normalizedPath, event.eventType, event.filename || normalizedPath);
        }
      })();
    }

    if (!this.listeners.has(normalizedPath)) {
      this.listeners.set(normalizedPath, new Set());
    }

    this.listeners.get(normalizedPath)!.add(callback);

    // Return unwatch function
    return () => {
      this.unwatch(normalizedPath, callback);
    };
  }

  /**
   * Handle file system event with debouncing
   */
  private handleEvent(filePath: string, event: string, filename: string): void {
    const key = `${filePath}:${event}:${filename}`;

    // Clear existing timer
    const existingTimer = this.debounceTimers.get(key);
    if (existingTimer) {
      clearTimeout(existingTimer);
    }

    // Set new timer
    const timer = setTimeout(() => {
      const listeners = this.listeners.get(filePath);
      if (listeners) {
        listeners.forEach(callback => callback(event, filename));
      }
      this.debounceTimers.delete(key);
    }, this.debounceDelay);

    this.debounceTimers.set(key, timer);
  }

  /**
   * Stop watching a file
   */
  unwatch(filePath: string, callback?: (event: string, filename: string) => void): void {
    const normalizedPath = normalizePath(filePath);
    const listeners = this.listeners.get(normalizedPath);

    if (callback && listeners) {
      listeners.delete(callback);

      if (listeners.size === 0) {
        this.listeners.delete(normalizedPath);
        this.closeWatcher(normalizedPath);
      }
    } else {
      this.listeners.delete(normalizedPath);
      this.closeWatcher(normalizedPath);
    }
  }

  /**
   * Close watcher for file
   */
  private closeWatcher(filePath: string): void {
    const watcher = this.watchers.get(filePath);
    if (watcher) {
      watcher.close();
      this.watchers.delete(filePath);
    }
  }

  /**
   * Stop watching all files
   */
  unwatchAll(): void {
    for (const watcher of this.watchers.values()) {
      watcher.close();
    }

    this.watchers.clear();
    this.listeners.clear();

    for (const timer of this.debounceTimers.values()) {
      clearTimeout(timer);
    }

    this.debounceTimers.clear();
  }

  /**
   * Get watched files
   */
  getWatchedFiles(): string[] {
    return Array.from(this.watchers.keys());
  }
}

/**
 * Batch file operations
 */
export class BatchFileOperations {
  private fsManager: FileSystemManager;

  constructor(fsManager?: FileSystemManager) {
    this.fsManager = fsManager || new FileSystemManager();
  }

  /**
   * Read multiple files in parallel
   */
  async readMultiple(
    filePaths: string[],
    options: { concurrency?: number } = {}
  ): Promise<Map<string, string>> {
    return this.fsManager.readFiles(filePaths, options);
  }

  /**
   * Write multiple files in parallel
   */
  async writeMultiple(
    files: Array<{ path: string; content: string }>,
    options: { concurrency?: number } = {}
  ): Promise<void> {
    await batchProcess(
      files,
      async file => {
        await this.fsManager.writeFile(file.path, file.content);
      },
      { concurrency: options.concurrency || 10 }
    );
  }

  /**
   * Copy multiple files in parallel
   */
  async copyMultiple(
    operations: Array<{ src: string; dest: string }>,
    options: { concurrency?: number } = {}
  ): Promise<void> {
    await batchProcess(
      operations,
      async op => {
        await this.fsManager.copyFile(op.src, op.dest);
      },
      { concurrency: options.concurrency || 10 }
    );
  }

  /**
   * Delete multiple files in parallel
   */
  async deleteMultiple(filePaths: string[], options: { concurrency?: number } = {}): Promise<void> {
    await batchProcess(
      filePaths,
      async filePath => {
        await this.fsManager.deleteFile(filePath);
      },
      { concurrency: options.concurrency || 10 }
    );
  }
}

/**
 * Inter-MCP integration and communication layer
 * Enables MCPs to work together seamlessly
 */

import { MCPResult, PipelineStep, PipelineResult, MCPConfig } from '../types/index.js';
import { PerformanceMonitor } from '../performance/index.js';
import { MCPClient } from '../mcp-client/index.js';

/**
 * MCP Pipeline - orchestrates multiple MCP tools
 */
export class MCPPipeline {
  private steps: PipelineStep[] = [];
  private results: Map<string, MCPResult> = new Map();
  private performance: PerformanceMonitor = new PerformanceMonitor();
  private mcpClient: MCPClient = new MCPClient();
  private subPipelines: Map<string, MCPPipeline> = new Map();
  private executionOrder: string[] = [];
  private retryAttempts = new Map<string, number>();

  /**
   * Add a step to the pipeline
   */
  addStep(step: PipelineStep): this {
    this.steps.push(step);
    return this;
  }

  /**
   * Add a sub-pipeline
   */
  addSubPipeline(name: string, pipeline: MCPPipeline): this {
    this.subPipelines.set(name, pipeline);
    return this;
  }

  /**
   * Add conditional step
   */
  addConditionalStep(step: PipelineStep & { condition?: () => boolean }): this {
    if (!step.condition || step.condition()) {
      this.addStep(step);
    }
    return this;
  }

  /**
   * Execute the pipeline
   */
  async execute(): Promise<PipelineResult> {
    this.performance.start();
    const stepResults: PipelineResult['steps'] = [];
    const errors: string[] = [];

    // Sort steps by dependencies
    const sortedSteps = this.topologicalSort(this.steps);

    for (const step of sortedSteps) {
      this.executionOrder.push(step.name);

      // Check dependencies
      if (step.dependsOn) {
        const missingDeps = step.dependsOn.filter(dep => !this.results.has(dep));
        if (missingDeps.length > 0) {
          const error = `Step "${step.name}" missing dependencies: ${missingDeps.join(', ')}`;
          errors.push(error);
          continue;
        }
      }

      // Execute step with retries
      const stepStart = Date.now();
      this.performance.mark(`step-${step.name}-start`);

      let attempts = 0;
      const maxRetries = (step as any).retryCount || 0;

      while (attempts <= maxRetries) {
        try {
          const result = await this.executeStep(step);
          this.results.set(step.name, result);

          stepResults.push({
            name: step.name,
            tool: step.tool,
            result,
            duration: Date.now() - stepStart,
          });

          this.performance.mark(`step-${step.name}-end`);
          break;
        } catch (error) {
          attempts++;
          this.retryAttempts.set(step.name, attempts);

          if (attempts > maxRetries) {
            if ((step as any).continueOnError) {
              stepResults.push({
                name: step.name,
                tool: step.tool,
                result: {
                  success: false,
                  error: (error as Error).message,
                  timestamp: new Date().toISOString(),
                },
                duration: Date.now() - stepStart,
              });
            } else {
              errors.push(`Step "${step.name}" failed: ${(error as Error).message}`);
            }
            break;
          }
        }
      }
    }

    const metrics = this.performance.stop();

    return {
      steps: stepResults,
      totalDuration: metrics.duration,
      success: errors.length === 0,
      errors,
      results: stepResults.length > 0 ? stepResults.map(s => s.result) : undefined,
      metadata: {
        duration: metrics.duration,
        executionOrder: this.executionOrder,
        failedStep:
          errors.length > 0 ? this.executionOrder[this.executionOrder.length - 1] : undefined,
        retryAttempts: Object.fromEntries(this.retryAttempts),
      },
    } as any;
  }

  /**
   * Execute a single step - NOW CALLS REAL MCPs
   */
  private async executeStep(step: PipelineStep): Promise<MCPResult> {
    // Get input from previous steps
    let input = step.input;
    if (step.dependsOn && step.dependsOn.length > 0) {
      input = step.dependsOn.map(dep => this.results.get(dep)?.data);
    }

    try {
      // Invoke actual MCP via stdio
      const action = step.config.action || 'execute';
      const params = input || step.config.params || {};

      const data = await this.mcpClient.invoke(step.tool, action, params);

      return {
        success: true,
        data,
        timestamp: new Date().toISOString(),
      };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : String(error),
        timestamp: new Date().toISOString(),
      };
    }
  }

  /**
   * Get result from a specific step
   */
  getResult(stepName: string): MCPResult | undefined {
    return this.results.get(stepName);
  }

  /**
   * Get all results
   */
  getAllResults(): Map<string, MCPResult> {
    return this.results;
  }

  /**
   * Clear pipeline
   */
  clear(): void {
    this.steps = [];
    this.results.clear();
    this.performance.reset();
    this.executionOrder = [];
    this.retryAttempts.clear();
  }

  /**
   * Topological sort for dependency resolution
   */
  private topologicalSort(steps: PipelineStep[]): PipelineStep[] {
    const sorted: PipelineStep[] = [];
    const visited = new Set<string>();
    const visiting = new Set<string>();

    const visit = (stepName: string) => {
      if (visited.has(stepName)) return;
      if (visiting.has(stepName)) {
        throw new Error(`Circular dependency detected: ${stepName}`);
      }

      visiting.add(stepName);
      const step = steps.find(s => s.name === stepName);

      if (step?.dependsOn) {
        for (const dep of step.dependsOn) {
          visit(dep);
        }
      }

      visiting.delete(stepName);
      visited.add(stepName);

      if (step) {
        sorted.push(step);
      }
    };

    for (const step of steps) {
      visit(step.name);
    }

    return sorted;
  }
}

/**
 * MCP Integration Manager - manages MCP tool interactions
 */
export class MCPIntegration {
  private tools: Map<string, any> = new Map();
  private cache: Map<string, any> = new Map();

  /**
   * Register an MCP tool
   */
  registerTool(name: string, tool: any): void {
    this.tools.set(name, tool);
  }

  /**
   * Get registered tool
   */
  getTool(name: string): any {
    return this.tools.get(name);
  }

  /**
   * Execute multiple MCPs in sequence
   */
  async sequence(steps: Array<{ tool: string; method: string; args: any[] }>): Promise<any[]> {
    const results: any[] = [];

    for (const step of steps) {
      const tool = this.getTool(step.tool);
      if (!tool) {
        throw new Error(`Tool "${step.tool}" not registered`);
      }

      const result = await tool[step.method](...step.args);
      results.push(result);
    }

    return results;
  }

  /**
   * Execute multiple MCPs in parallel
   */
  async parallel(steps: Array<{ tool: string; method: string; args: any[] }>): Promise<any[]> {
    const promises = steps.map(async step => {
      const tool = this.getTool(step.tool);
      if (!tool) {
        throw new Error(`Tool "${step.tool}" not registered`);
      }
      return tool[step.method](...step.args);
    });

    return Promise.all(promises);
  }

  /**
   * Chain MCP operations - output of one becomes input of next
   */
  async chain(
    initialInput: any,
    operations: Array<{ tool: string; method: string; transform?: (result: any) => any }>
  ): Promise<any> {
    let result = initialInput;

    for (const op of operations) {
      const tool = this.getTool(op.tool);
      if (!tool) {
        throw new Error(`Tool "${op.tool}" not registered`);
      }

      result = await tool[op.method](result);

      if (op.transform) {
        result = op.transform(result);
      }
    }

    return result;
  }

  /**
   * Cache result from MCP operation
   */
  cacheResult(key: string, value: any, ttl: number = 3600000): void {
    this.cache.set(key, {
      value,
      timestamp: Date.now(),
      ttl,
    });
  }

  /**
   * Get cached result
   */
  getCachedResult(key: string): any | null {
    const cached = this.cache.get(key);
    if (!cached) return null;

    const age = Date.now() - cached.timestamp;
    if (age > cached.ttl) {
      this.cache.delete(key);
      return null;
    }

    return cached.value;
  }

  /**
   * Clear cache
   */
  clearCache(): void {
    this.cache.clear();
  }
}

/**
 * Create a workflow that combines multiple MCPs
 */
export class MCPWorkflow {
  private name: string;
  private steps: Array<{
    name: string;
    tool: string;
    action: string;
    config?: MCPConfig;
    condition?: (results: any) => boolean;
  }> = [];

  constructor(name: string) {
    this.name = name;
  }

  /**
   * Add a step to the workflow
   */
  step(
    name: string,
    tool: string,
    action: string,
    config?: MCPConfig,
    condition?: (results: any) => boolean
  ): this {
    this.steps.push({ name, tool, action, config, condition });
    return this;
  }

  /**
   * Execute the workflow
   */
  async run(integration: MCPIntegration, initialData: any): Promise<any> {
    const results: any = { initialData };
    const performance = new PerformanceMonitor();
    performance.start();

    for (const step of this.steps) {
      // Check condition
      if (step.condition && !step.condition(results)) {
        // Step skipped - condition not met
        continue;
      }

      // Executing workflow step

      const tool = integration.getTool(step.tool);
      if (!tool) {
        throw new Error(`Tool "${step.tool}" not found`);
      }

      const stepStart = Date.now();
      const result = await tool[step.action](results, step.config);
      const stepDuration = Date.now() - stepStart;

      results[step.name] = {
        data: result,
        duration: stepDuration,
      };

      performance.mark(`step-${step.name}`);
    }

    const metrics = performance.stop();

    return {
      workflow: this.name,
      results,
      performance: metrics,
    };
  }

  /**
   * Get workflow definition
   */
  getDefinition(): any {
    return {
      name: this.name,
      steps: this.steps.map(s => ({
        name: s.name,
        tool: s.tool,
        action: s.action,
      })),
    };
  }
}

/**
 * Event bus for MCP communication
 */
export class MCPEventBus {
  private listeners: Map<string, Set<(...args: any[]) => void>> = new Map();

  /**
   * Subscribe to an event
   */
  on(event: string, callback: (...args: any[]) => void): () => void {
    if (!this.listeners.has(event)) {
      this.listeners.set(event, new Set());
    }

    this.listeners.get(event)!.add(callback);

    // Return unsubscribe function
    return () => {
      this.listeners.get(event)?.delete(callback);
    };
  }

  /**
   * Emit an event
   */
  emit(event: string, data: any): void {
    const callbacks = this.listeners.get(event);
    if (callbacks) {
      callbacks.forEach(callback => callback(data));
    }
  }

  /**
   * Subscribe to event once
   */
  once(event: string, callback: (...args: any[]) => void): void {
    const unsubscribe = this.on(event, (data: any) => {
      callback(data);
      unsubscribe();
    });
  }

  /**
   * Remove all listeners for an event
   */
  off(event: string): void {
    this.listeners.delete(event);
  }

  /**
   * Clear all listeners
   */
  clear(): void {
    this.listeners.clear();
  }

  /**
   * Get listener count for event
   */
  listenerCount(event: string): number {
    return this.listeners.get(event)?.size || 0;
  }
}

/**
 * MCP Client - Invoke other MCP tools via stdio
 * Enables MCP-to-MCP communication for workflow orchestration
 */

import { spawn, ChildProcess } from 'child_process';
import { resolve, dirname } from 'path';
import { fileURLToPath } from 'url';
import { readFileSync } from 'fs';

export interface MCPRequest {
  jsonrpc: '2.0';
  id?: number | string;
  method: string;
  params: {
    name: string;
    arguments: any;
  };
}

export interface MCPResponse {
  jsonrpc: '2.0';
  id?: number | string;
  result?: any;
  error?: {
    code: number;
    message: string;
    data?: any;
  };
}

/**
 * Client for invoking MCP tools via stdio
 */
export class MCPClient {
  private requestId = 0;
  private defaultTimeout = 30000; // 30 seconds

  /**
   * Invoke an MCP tool
   *
   * @param mcpName - Name of MCP (e.g., 'smart-reviewer')
   * @param toolName - Tool to call (e.g., 'review_file')
   * @param params - Tool parameters
   * @param timeout - Optional timeout in milliseconds
   * @returns Tool result
   */
  async invoke(mcpName: string, toolName: string, params: any, timeout?: number): Promise<any> {
    // 1. Resolve MCP binary path
    const mcpPath = this.resolveMCPBinary(mcpName);

    // 2. Spawn MCP process
    const child = spawn('node', [mcpPath], {
      stdio: ['pipe', 'pipe', 'pipe'],
      env: process.env,
    });

    try {
      // 3. Send request
      const request: MCPRequest = {
        jsonrpc: '2.0',
        id: ++this.requestId,
        method: 'tools/call',
        params: {
          name: toolName,
          arguments: params,
        },
      };

      child.stdin.write(JSON.stringify(request) + '\n');
      child.stdin.end();

      // 4. Read response (with timeout)
      const response = await this.readResponse(child, timeout || this.defaultTimeout);

      // 5. Handle errors
      if (response.error) {
        throw new Error(`MCP error: ${response.error.message}`);
      }

      return response.result;
    } finally {
      // 6. Cleanup
      if (!child.killed) {
        child.kill();
      }
    }
  }

  /**
   * Resolve MCP binary path from package name
   */
  private resolveMCPBinary(mcpName: string): string {
    const packageMap: Record<string, string> = {
      'smart-reviewer': '@j0kz/smart-reviewer-mcp',
      'security-scanner': '@j0kz/security-scanner-mcp',
      'test-generator': '@j0kz/test-generator-mcp',
      'architecture-analyzer': '@j0kz/architecture-analyzer-mcp',
      'refactor-assistant': '@j0kz/refactor-assistant-mcp',
      'doc-generator': '@j0kz/doc-generator-mcp',
      'api-designer': '@j0kz/api-designer-mcp',
      'db-schema': '@j0kz/db-schema-mcp',
    };

    const packageName = packageMap[mcpName];
    if (!packageName) {
      throw new Error(`Unknown MCP: ${mcpName}`);
    }

    // Try to resolve from node_modules
    try {
      // Resolve package.json path
      let packageJsonPath: string;

      try {
        // Try CommonJS require.resolve first
        packageJsonPath = require.resolve(`${packageName}/package.json`);
      } catch {
        // Fallback: construct path manually for ESM environments
        const __filename = fileURLToPath(import.meta.url);
        const __dirname = dirname(__filename);
        packageJsonPath = resolve(
          __dirname,
          '../../../..',
          'node_modules',
          packageName,
          'package.json'
        );
      }

      const packageDir = dirname(packageJsonPath);
      const packageJson = JSON.parse(readFileSync(packageJsonPath, 'utf-8'));

      // Get binary path from package.json bin field
      const binName = Object.keys(packageJson.bin)[0];
      const binPath = packageJson.bin[binName];

      return resolve(packageDir, binPath);
    } catch {
      throw new Error(`MCP not installed: ${packageName}. Run: npm install ${packageName}`);
    }
  }

  /**
   * Read MCP response from stdout (with timeout)
   */
  private async readResponse(child: ChildProcess, timeout: number): Promise<MCPResponse> {
    return new Promise((resolve, reject) => {
      let output = '';
      let errorOutput = '';

      // Timeout
      const timer = setTimeout(() => {
        child.kill();
        reject(new Error(`MCP invocation timeout (${timeout}ms)`));
      }, timeout);

      // Read stdout
      child.stdout?.on('data', chunk => {
        output += chunk.toString();
      });

      // Read stderr (for debugging, not errors)
      child.stderr?.on('data', chunk => {
        errorOutput += chunk.toString();
      });

      // Process exit
      child.on('close', code => {
        clearTimeout(timer);

        if (code !== 0 && code !== null) {
          reject(new Error(`MCP exited with code ${code}: ${errorOutput}`));
          return;
        }

        // Parse response (may have multiple JSON objects, take last one)
        const lines = output.trim().split('\n');
        const lastLine = lines[lines.length - 1];

        try {
          const response = JSON.parse(lastLine);
          resolve(response);
        } catch {
          reject(new Error(`Invalid MCP response: ${lastLine}`));
        }
      });

      child.on('error', error => {
        clearTimeout(timer);
        reject(error);
      });
    });
  }

  /**
   * Check if an MCP is installed and available
   */
  isInstalled(mcpName: string): boolean {
    try {
      this.resolveMCPBinary(mcpName);
      return true;
    } catch {
      return false;
    }
  }

  /**
   * Get list of available (installed) MCPs
   */
  getAvailableMCPs(): string[] {
    const allMCPs = [
      'smart-reviewer',
      'security-scanner',
      'test-generator',
      'architecture-analyzer',
      'refactor-assistant',
      'doc-generator',
      'api-designer',
      'db-schema',
    ];

    return allMCPs.filter(mcp => this.isInstalled(mcp));
  }
}

/**
 * MCP Protocol Validation Layer
 * Ensures all MCP tools comply with protocol specifications
 */

import { z } from 'zod';

/**
 * MCP Tool Schema definition based on Model Context Protocol spec
 */
export const MCPToolSchema = z.object({
  name: z
    .string()
    .min(1)
    .regex(/^[a-z][a-z0-9_]*$/),
  description: z.string().min(1),
  inputSchema: z.object({
    type: z.literal('object'),
    properties: z.record(z.any()),
    required: z.array(z.string()).optional(),
    additionalProperties: z.boolean().optional(),
  }),
});

/**
 * MCP Request Schema
 */
export const MCPRequestSchema = z.object({
  jsonrpc: z.literal('2.0'),
  method: z.string(),
  params: z.object({
    name: z.string(),
    arguments: z.record(z.any()).optional(),
  }),
  id: z.union([z.string(), z.number()]),
});

/**
 * MCP Response Schema
 */
export const MCPResponseSchema = z.object({
  jsonrpc: z.literal('2.0'),
  result: z.any().optional(),
  error: z
    .object({
      code: z.number(),
      message: z.string(),
      data: z.any().optional(),
    })
    .optional(),
  id: z.union([z.string(), z.number()]),
});

export interface MCPValidationResult {
  valid: boolean;
  errors?: string[];
  warnings?: string[];
}

export interface MCPToolDefinition {
  name: string;
  description: string;
  inputSchema: {
    type: 'object';
    properties: Record<string, any>;
    required?: string[];
    additionalProperties?: boolean;
  };
}

export interface MCPProtocolRequest {
  jsonrpc: '2.0';
  method: string;
  params: {
    name: string;
    arguments?: Record<string, any>;
  };
  id: string | number;
}

export interface MCPProtocolResponse {
  jsonrpc: '2.0';
  result?: any;
  error?: {
    code: number;
    message: string;
    data?: any;
  };
  id: string | number;
}

/**
 * MCP Protocol Validator
 * Validates tools, requests, and responses against MCP specification
 */
export class MCPProtocolValidator {
  private knownTools = new Map<string, MCPToolDefinition>();

  /**
   * Register a tool for validation
   */
  registerTool(tool: MCPToolDefinition): MCPValidationResult {
    const result = this.validateToolSchema(tool);
    if (result.valid) {
      this.knownTools.set(tool.name, tool);
    }
    return result;
  }

  /**
   * Validate a tool definition against MCP schema
   */
  validateToolSchema(tool: unknown): MCPValidationResult {
    try {
      const parsed = MCPToolSchema.parse(tool);

      const warnings: string[] = [];

      // Check for best practices
      if (parsed.description.length < 20) {
        warnings.push('Tool description should be more detailed (20+ characters)');
      }

      if (
        !parsed.inputSchema.properties ||
        Object.keys(parsed.inputSchema.properties).length === 0
      ) {
        warnings.push('Tool has no input parameters defined');
      }

      // Validate parameter naming conventions
      for (const [paramName, paramSchema] of Object.entries(parsed.inputSchema.properties || {})) {
        if (!/^[a-z][a-zA-Z0-9]*$/.test(paramName)) {
          warnings.push(`Parameter '${paramName}' should use camelCase naming`);
        }

        // Check for parameter descriptions
        if (typeof paramSchema === 'object' && !paramSchema.description) {
          warnings.push(`Parameter '${paramName}' lacks a description`);
        }
      }

      return {
        valid: true,
        warnings: warnings.length > 0 ? warnings : undefined,
      };
    } catch (error) {
      if (error instanceof z.ZodError) {
        return {
          valid: false,
          errors: error.errors.map(e => `${e.path.join('.')}: ${e.message}`),
        };
      }
      return {
        valid: false,
        errors: [`Unknown validation error: ${error}`],
      };
    }
  }

  /**
   * Validate an MCP request
   */
  validateRequest(request: unknown): MCPValidationResult {
    try {
      const parsed = MCPRequestSchema.parse(request);

      // Check if tool exists
      if (parsed.method === 'tools/call') {
        if (!this.knownTools.has(parsed.params.name)) {
          return {
            valid: false,
            errors: [`Unknown tool: ${parsed.params.name}`],
          };
        }

        // Validate arguments against tool schema
        const tool = this.knownTools.get(parsed.params.name)!;
        const result = this.validateArguments(parsed.params.arguments || {}, tool.inputSchema);

        if (!result.valid) {
          return result;
        }
      }

      return { valid: true };
    } catch (error) {
      if (error instanceof z.ZodError) {
        return {
          valid: false,
          errors: error.errors.map(e => `${e.path.join('.')}: ${e.message}`),
        };
      }
      return {
        valid: false,
        errors: [`Unknown validation error: ${error}`],
      };
    }
  }

  /**
   * Validate arguments against a tool's input schema
   */
  private validateArguments(
    args: Record<string, any>,
    schema: MCPToolDefinition['inputSchema']
  ): MCPValidationResult {
    const errors: string[] = [];

    // Check required fields
    if (schema.required) {
      for (const field of schema.required) {
        if (!(field in args)) {
          errors.push(`Required field '${field}' is missing`);
        }
      }
    }

    // Check for unknown fields
    if (schema.additionalProperties === false) {
      for (const field of Object.keys(args)) {
        if (!schema.properties[field]) {
          errors.push(`Unknown field '${field}' not allowed`);
        }
      }
    }

    // Type validation for each field
    for (const [field, value] of Object.entries(args)) {
      const fieldSchema = schema.properties[field];
      if (fieldSchema) {
        const typeError = this.validateType(value, fieldSchema, field);
        if (typeError) {
          errors.push(typeError);
        }
      }
    }

    return {
      valid: errors.length === 0,
      errors: errors.length > 0 ? errors : undefined,
    };
  }

  /**
   * Validate a value against a type schema
   */
  private validateType(value: any, schema: any, fieldName: string): string | null {
    if (!schema.type) return null;

    const actualType = Array.isArray(value) ? 'array' : typeof value;

    switch (schema.type) {
      case 'string':
        if (typeof value !== 'string') {
          return `Field '${fieldName}' must be a string, got ${actualType}`;
        }
        if (schema.minLength && value.length < schema.minLength) {
          return `Field '${fieldName}' must be at least ${schema.minLength} characters`;
        }
        if (schema.maxLength && value.length > schema.maxLength) {
          return `Field '${fieldName}' must be at most ${schema.maxLength} characters`;
        }
        if (schema.pattern && !new RegExp(schema.pattern).test(value)) {
          return `Field '${fieldName}' does not match pattern ${schema.pattern}`;
        }
        break;

      case 'number':
      case 'integer':
        if (typeof value !== 'number') {
          return `Field '${fieldName}' must be a number, got ${actualType}`;
        }
        if (schema.type === 'integer' && !Number.isInteger(value)) {
          return `Field '${fieldName}' must be an integer`;
        }
        if (schema.minimum !== undefined && value < schema.minimum) {
          return `Field '${fieldName}' must be at least ${schema.minimum}`;
        }
        if (schema.maximum !== undefined && value > schema.maximum) {
          return `Field '${fieldName}' must be at most ${schema.maximum}`;
        }
        break;

      case 'boolean':
        if (typeof value !== 'boolean') {
          return `Field '${fieldName}' must be a boolean, got ${actualType}`;
        }
        break;

      case 'array':
        if (!Array.isArray(value)) {
          return `Field '${fieldName}' must be an array, got ${actualType}`;
        }
        if (schema.minItems && value.length < schema.minItems) {
          return `Field '${fieldName}' must have at least ${schema.minItems} items`;
        }
        if (schema.maxItems && value.length > schema.maxItems) {
          return `Field '${fieldName}' must have at most ${schema.maxItems} items`;
        }
        break;

      case 'object':
        if (typeof value !== 'object' || value === null || Array.isArray(value)) {
          return `Field '${fieldName}' must be an object, got ${actualType}`;
        }
        break;
    }

    return null;
  }

  /**
   * Validate an MCP response
   */
  validateResponse(response: unknown, requestId?: string | number): MCPValidationResult {
    try {
      const parsed = MCPResponseSchema.parse(response);

      // Check that either result or error is present, not both
      if (parsed.result !== undefined && parsed.error !== undefined) {
        return {
          valid: false,
          errors: ['Response cannot have both result and error'],
        };
      }

      if (parsed.result === undefined && parsed.error === undefined) {
        return {
          valid: false,
          errors: ['Response must have either result or error'],
        };
      }

      // Validate ID matches request
      if (requestId !== undefined && parsed.id !== requestId) {
        return {
          valid: false,
          errors: [`Response ID ${parsed.id} does not match request ID ${requestId}`],
        };
      }

      return { valid: true };
    } catch (error) {
      if (error instanceof z.ZodError) {
        return {
          valid: false,
          errors: error.errors.map(e => `${e.path.join('.')}: ${e.message}`),
        };
      }
      return {
        valid: false,
        errors: [`Unknown validation error: ${error}`],
      };
    }
  }

  /**
   * Get all registered tools
   */
  getRegisteredTools(): MCPToolDefinition[] {
    return Array.from(this.knownTools.values());
  }

  /**
   * Clear all registered tools
   */
  clearTools(): void {
    this.knownTools.clear();
  }
}

/**
 * Common patterns used across MCP tools
 * Reduces code duplication and enforces consistency
 */

import { MCPError, getErrorMessage } from '../errors/index.js';

/**
 * Standard result format for MCP operations
 */
export interface MCPOperationResult<T = any> {
  success: boolean;
  data?: T;
  error?: string;
  errorCode?: string;
  metadata?: Record<string, any>;
}

/**
 * Standard MCP response format
 */
export interface MCPToolResponse {
  content: Array<{
    type: 'text';
    text: string;
  }>;
}

/**
 * Creates a successful MCP result
 */
export function successResult<T>(data: T, metadata?: Record<string, any>): MCPOperationResult<T> {
  return {
    success: true,
    data,
    metadata,
  };
}

/**
 * Creates an error MCP result
 */
export function errorResult(error: unknown, code?: string): MCPOperationResult {
  const message = getErrorMessage(error);
  const errorCode = error instanceof MCPError ? error.code : code || 'UNKNOWN';

  return {
    success: false,
    error: message,
    errorCode,
  };
}

/**
 * Wraps an async operation with standard error handling
 */
export async function withErrorHandling<T>(
  operation: () => Promise<T>,
  errorCode?: string
): Promise<MCPOperationResult<T>> {
  try {
    const result = await operation();
    return successResult(result);
  } catch (error) {
    return errorResult(error, errorCode);
  }
}

/**
 * Creates an MCP tool response from a result
 */
export function createToolResponse(result: MCPOperationResult): MCPToolResponse {
  return {
    content: [
      {
        type: 'text',
        text: JSON.stringify(result, null, 2),
      },
    ],
  };
}

/**
 * Handles file operations with consistent error handling
 */
export async function safeFileOperation<T>(
  operation: () => Promise<T>,
  filePath: string
): Promise<MCPOperationResult<T>> {
  try {
    const result = await operation();
    return successResult(result, { filePath });
  } catch (error: any) {
    const errorCode =
      error.code === 'ENOENT'
        ? 'FILE_NOT_FOUND'
        : error.code === 'EACCES'
          ? 'PERMISSION_DENIED'
          : error.code === 'EISDIR'
            ? 'IS_DIRECTORY'
            : 'FILE_ERROR';

    return {
      success: false,
      error: `File operation failed for ${filePath}: ${getErrorMessage(error)}`,
      errorCode,
      metadata: { filePath },
    };
  }
}

/**
 * Validates required parameters for MCP tools
 */
export function validateRequiredParams(
  params: Record<string, any>,
  required: string[]
): MCPOperationResult<void> {
  const missing = required.filter(param => !params[param]);

  if (missing.length > 0) {
    return {
      success: false,
      error: `Missing required parameters: ${missing.join(', ')}`,
      errorCode: 'MISSING_PARAMS',
    };
  }

  return { success: true };
}

/**
 * Executes steps with dependency management
 */
export async function executeWithDependencies<T>(
  steps: Array<{
    name: string;
    execute: () => Promise<T>;
    dependsOn?: string[];
  }>
): Promise<MCPOperationResult<Map<string, T>>> {
  const results = new Map<string, T>();
  const completed = new Set<string>();

  for (const step of steps) {
    // Check dependencies
    if (step.dependsOn) {
      const missingDeps = step.dependsOn.filter(dep => !completed.has(dep));
      if (missingDeps.length > 0) {
        return {
          success: false,
          error: `Step '${step.name}' has unmet dependencies: ${missingDeps.join(', ')}`,
          errorCode: 'DEPENDENCY_ERROR',
        };
      }
    }

    // Execute step
    try {
      const result = await step.execute();
      results.set(step.name, result);
      completed.add(step.name);
    } catch (error) {
      return {
        success: false,
        error: `Step '${step.name}' failed: ${getErrorMessage(error)}`,
        errorCode: 'STEP_FAILED',
        metadata: { failedStep: step.name },
      };
    }
  }

  return successResult(results);
}

/**
 * Batches operations for better performance
 */
export async function batchOperation<T, R>(
  items: T[],
  operation: (item: T) => Promise<R>,
  batchSize: number = 10
): Promise<MCPOperationResult<R[]>> {
  const results: R[] = [];
  const errors: Array<{ index: number; error: string }> = [];

  for (let i = 0; i < items.length; i += batchSize) {
    const batch = items.slice(i, i + batchSize);
    const batchPromises = batch.map(async (item, index) => {
      try {
        return await operation(item);
      } catch (error) {
        errors.push({
          index: i + index,
          error: getErrorMessage(error),
        });
        return null;
      }
    });

    const batchResults = await Promise.all(batchPromises);
    results.push(...(batchResults.filter(r => r !== null) as R[]));
  }

  if (errors.length > 0) {
    return {
      success: false,
      error: `Batch operation had ${errors.length} failures`,
      errorCode: 'BATCH_PARTIAL_FAILURE',
      data: results,
      metadata: { errors },
    };
  }

  return successResult(results);
}

/**
 * Retries an operation with exponential backoff
 */
export async function retryOperation<T>(
  operation: () => Promise<T>,
  maxRetries: number = 3,
  initialDelay: number = 1000
): Promise<MCPOperationResult<T>> {
  let lastError: unknown;

  for (let i = 0; i < maxRetries; i++) {
    try {
      const result = await operation();
      return successResult(result);
    } catch (error) {
      lastError = error;

      if (i < maxRetries - 1) {
        const delay = initialDelay * Math.pow(2, i);
        await new Promise(resolve => setTimeout(resolve, delay));
      }
    }
  }

  return {
    success: false,
    error: `Operation failed after ${maxRetries} retries: ${getErrorMessage(lastError)}`,
    errorCode: 'MAX_RETRIES_EXCEEDED',
  };
}

/**
 * Validates file size before processing
 */
export function validateFileSize(
  size: number,
  maxSize: number = 100 * 1024 * 1024 // 100MB default
): MCPOperationResult<void> {
  if (size > maxSize) {
    return {
      success: false,
      error: `File size ${size} bytes exceeds maximum of ${maxSize} bytes`,
      errorCode: 'FILE_TOO_LARGE',
    };
  }

  return { success: true };
}

/**
 * Merges multiple results into a single result
 */
export function mergeResults<T>(results: MCPOperationResult<T>[]): MCPOperationResult<T[]> {
  const errors = results.filter(r => !r.success);

  if (errors.length > 0) {
    return {
      success: false,
      error: `${errors.length} operations failed`,
      errorCode: 'MULTIPLE_FAILURES',
      metadata: {
        errors: errors.map(e => ({ error: e.error, code: e.errorCode })),
      },
    };
  }

  const data = results.filter(r => r.success && r.data !== undefined).map(r => r.data as T);

  return successResult(data);
}

/**
 * Performance benchmarking utilities for MCP packages
 */

export interface BenchmarkResult {
  name: string;
  iterations: number;
  totalTime: number;
  averageTime: number;
  minTime: number;
  maxTime: number;
  opsPerSecond: number;
}

export interface BenchmarkOptions {
  iterations?: number;
  warmup?: number;
  name?: string;
}

/**
 * Run a performance benchmark
 */
export async function benchmark(
  fn: () => void | Promise<void>,
  options: BenchmarkOptions = {}
): Promise<BenchmarkResult> {
  const { iterations = 100, warmup = 10, name = 'Benchmark' } = options;

  // Warmup runs
  for (let i = 0; i < warmup; i++) {
    await fn();
  }

  const times: number[] = [];
  let totalTime = 0;

  // Actual benchmark runs
  for (let i = 0; i < iterations; i++) {
    const start = performance.now();
    await fn();
    const duration = performance.now() - start;
    times.push(duration);
    totalTime += duration;
  }

  const averageTime = totalTime / iterations;
  const minTime = Math.min(...times);
  const maxTime = Math.max(...times);
  const opsPerSecond = 1000 / averageTime;

  return {
    name,
    iterations,
    totalTime,
    averageTime,
    minTime,
    maxTime,
    opsPerSecond,
  };
}

/**
 * Compare two benchmark results
 */
export function compareBenchmarks(
  baseline: BenchmarkResult,
  optimized: BenchmarkResult
): {
  speedup: number;
  percentFaster: number;
  timeSaved: number;
  verdict: string;
} {
  const speedup = baseline.averageTime / optimized.averageTime;
  const percentFaster = (speedup - 1) * 100;
  const timeSaved = baseline.averageTime - optimized.averageTime;

  let verdict = '';
  if (speedup >= 2) {
    verdict = ' Major improvement';
  } else if (speedup >= 1.5) {
    verdict = ' Significant improvement';
  } else if (speedup >= 1.1) {
    verdict = ' Noticeable improvement';
  } else if (speedup >= 0.95) {
    verdict = ' Negligible difference';
  } else {
    verdict = '  Performance regression';
  }

  return {
    speedup,
    percentFaster,
    timeSaved,
    verdict,
  };
}

/**
 * Format benchmark result for display
 */
export function formatBenchmarkResult(result: BenchmarkResult): string {
  return `
${result.name}
${'='.repeat(60)}
Iterations:     ${result.iterations}
Total time:     ${result.totalTime.toFixed(2)}ms
Average time:   ${result.averageTime.toFixed(3)}ms
Min time:       ${result.minTime.toFixed(3)}ms
Max time:       ${result.maxTime.toFixed(3)}ms
Ops/second:     ${result.opsPerSecond.toFixed(0)}
  `.trim();
}

/**
 * Run multiple benchmarks and compare
 */
export async function benchmarkSuite(
  benchmarks: Array<{
    name: string;
    fn: () => void | Promise<void>;
    options?: BenchmarkOptions;
  }>
): Promise<BenchmarkResult[]> {
  console.log('Running benchmark suite...\n');

  const results: BenchmarkResult[] = [];

  for (const bench of benchmarks) {
    const result = await benchmark(bench.fn, {
      ...bench.options,
      name: bench.name,
    });
    results.push(result);
    console.log(formatBenchmarkResult(result));
    console.log('');
  }

  return results;
}

/**
 * Performance optimization utilities for MCP tools
 */

import { PerformanceMetrics } from '../types/index.js';

/**
 * Performance monitor for tracking execution metrics
 */
export class PerformanceMonitor {
  private startTime: number = 0;
  private startMemory: number = 0;
  private metrics: Map<string, number[]> = new Map();

  /**
   * Start monitoring performance
   */
  start(): void {
    this.startTime = Date.now();
    this.startMemory = process.memoryUsage().heapUsed;
  }

  /**
   * Stop monitoring and return metrics
   */
  stop(): PerformanceMetrics {
    const endTime = Date.now();
    const endMemory = process.memoryUsage().heapUsed;

    return {
      startTime: this.startTime,
      endTime,
      duration: endTime - this.startTime,
      memoryUsed: endMemory - this.startMemory,
    };
  }

  /**
   * Mark a performance checkpoint
   */
  mark(name: string): void {
    const now = Date.now();
    if (!this.metrics.has(name)) {
      this.metrics.set(name, []);
    }
    this.metrics.get(name)!.push(now - this.startTime);
  }

  /**
   * Get metrics for a specific checkpoint
   */
  getMetrics(name: string): { avg: number; min: number; max: number; count: number } | null {
    const values = this.metrics.get(name);
    if (!values || values.length === 0) return null;

    return {
      avg: values.reduce((a, b) => a + b, 0) / values.length,
      min: Math.min(...values),
      max: Math.max(...values),
      count: values.length,
    };
  }

  /**
   * Get all metrics
   */
  getAllMetrics(): Record<string, any> {
    const result: Record<string, any> = {};
    for (const [name, _values] of this.metrics) {
      result[name] = this.getMetrics(name);
    }
    return result;
  }

  /**
   * Reset all metrics
   */
  reset(): void {
    this.metrics.clear();
    this.start();
  }
}

/**
 * Measure execution time of a function
 */
export async function measure<T>(
  fn: () => Promise<T>,
  _label?: string
): Promise<{ result: T; duration: number }> {
  const start = Date.now();
  const result = await fn();
  const duration = Date.now() - start;

  // Performance tracking - consumers can log if needed
  // if (label) {
  //   console.log(`[Performance] ${label}: ${duration}ms`);
  // }

  return { result, duration };
}

/**
 * Batch process items with concurrency limit
 */
export async function batchProcess<T, R>(
  items: T[],
  processor: (item: T) => Promise<R>,
  options: {
    concurrency?: number;
    onProgress?: (completed: number, total: number) => void;
  } = {}
): Promise<R[]> {
  const { concurrency = 5, onProgress } = options;
  const results: R[] = [];
  let completed = 0;

  for (let i = 0; i < items.length; i += concurrency) {
    const batch = items.slice(i, i + concurrency);
    const batchResults = await Promise.all(batch.map(processor));
    results.push(...batchResults);

    completed += batch.length;
    if (onProgress) {
      onProgress(completed, items.length);
    }
  }

  return results;
}

/**
 * Throttle function execution
 */
export function throttle<T extends (...args: any[]) => any>(
  func: T,
  limit: number
): (...args: Parameters<T>) => void {
  let inThrottle: boolean;
  return function (this: any, ...args: Parameters<T>) {
    if (!inThrottle) {
      func.apply(this, args);
      inThrottle = true;
      setTimeout(() => (inThrottle = false), limit);
    }
  };
}

/**
 * Memoize function results
 */
export function memoize<T extends (...args: any[]) => any>(
  fn: T,
  keyGenerator?: (...args: Parameters<T>) => string
): T {
  const cache = new Map<string, ReturnType<T>>();

  return ((...args: Parameters<T>) => {
    const key = keyGenerator ? keyGenerator(...args) : JSON.stringify(args);

    if (cache.has(key)) {
      return cache.get(key)!;
    }

    const result = fn(...args);
    cache.set(key, result);
    return result;
  }) as T;
}

/**
 * Process items in parallel with worker pool
 */
export async function parallelProcess<T, R>(
  items: T[],
  processor: (item: T, index: number) => Promise<R>,
  maxConcurrency: number = 10
): Promise<R[]> {
  const results: R[] = new Array(items.length);
  const executing: Promise<void>[] = [];

  for (let i = 0; i < items.length; i++) {
    const promise = processor(items[i], i).then(result => {
      results[i] = result;
    });

    executing.push(promise);

    if (executing.length >= maxConcurrency) {
      await Promise.race(executing);
      executing.splice(
        executing.findIndex(p => p === promise),
        1
      );
    }
  }

  await Promise.all(executing);
  return results;
}

/**
 * Lazy load resource
 */
export function lazyLoad<T>(loader: () => Promise<T>): () => Promise<T> {
  let cached: T | null = null;
  let loading: Promise<T> | null = null;

  return async () => {
    if (cached) return cached;
    if (loading) return loading;

    loading = loader();
    cached = await loading;
    loading = null;

    return cached;
  };
}

/**
 * Resource pool for reusing expensive objects
 */
export class ResourcePool<T> {
  private available: T[] = [];
  private inUse: Set<T> = new Set();
  private factory: () => T;
  private maxSize: number;

  constructor(factory: () => T, maxSize: number = 10) {
    this.factory = factory;
    this.maxSize = maxSize;
  }

  /**
   * Acquire resource from pool
   */
  acquire(): T {
    let resource: T;

    if (this.available.length > 0) {
      resource = this.available.pop()!;
    } else if (this.inUse.size < this.maxSize) {
      resource = this.factory();
    } else {
      throw new Error('Resource pool exhausted');
    }

    this.inUse.add(resource);
    return resource;
  }

  /**
   * Release resource back to pool
   */
  release(resource: T): void {
    if (this.inUse.has(resource)) {
      this.inUse.delete(resource);
      this.available.push(resource);
    }
  }

  /**
   * Get pool statistics
   */
  getStats(): { available: number; inUse: number; total: number } {
    return {
      available: this.available.length,
      inUse: this.inUse.size,
      total: this.available.length + this.inUse.size,
    };
  }
}

